import { Fragment } from "react";
import { useEffect, useState } from "react";
import "./App.css";
//Custom HooksetValue
const usePersistentState = (key, initialState) => {
  const [value, setValue] = useState(localStorage.getItem(key) || initialState);

  useEffect(() => {
    localStorage.setItem(key, value);
  }, [key, value]);

  return [value, setValue];
};

const App = () => {
  const stories = [
    {
      title: "React",
      url: "https://reactjs.org/",
      author: "Jordan Walke",
      num_comments: 3,
      points: 4,
      objectID: 0,
    },
    {
      title: "Redux",
      url: "https://redux.js.org/",
      author: "Dan Abramov, Andrew Clark",
      num_comments: 2,
      points: 5,
      objectID: 1,
    },
  ];

  const [searchTerm, setSearchTerm] = usePersistentState("search", "Redux");

  const handleChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const searchedStories = stories.filter((story) => {
    return story.title.toLowerCase().includes(searchTerm.toLowerCase());
  });

  return (
    <div>
      <h1>My React App</h1>
      <InputWithLabel
        onSearch={handleChange}
        search={searchTerm}
        id="search"
        label="Search"
      />
      <List list={searchedStories} />
    </div>
  );
};

export default App;

const InputWithLabel = ({ label, type = "text", id, search, onSearch }) => (
  <Fragment>
    <label htmlFor={id}>{label}</label>
    &nbsp;
    <input id={id} type={type} onChange={onSearch} value={search} />
  </Fragment>
); // Fragment can use <></>

const List = ({ list }) => (
  <ul>
    {list.map(({ objectID, ...item }) => (
      <Item key={objectID} {...item} />
    ))}
  </ul>
);

const Item = ({ title, author, url, num_comments, points }) => (
  <li>
    <a href={url}>{title}: </a>
    <span>{author} </span>
    <p>Comments: {num_comments}</p>
    <p>Points: {points}</p>
  </li>
);
