import React, { useState } from "react";
import {
  ProgressBar,
  Alert,
  Form,
  Button,
  Row,
  Col,
  Container,
  Image,
  Card,
} from "react-bootstrap";
import { storage } from "../../firebase/config";
import { ref, uploadBytesResumable, getDownloadURL } from "firebase/storage";
import { useAuth } from "../../contexts/AuthContext";

export default function Homepage() {
  const [images, setImages] = useState([]);
  const [urls, setUrls] = useState([]);
  const [progress, setProgress] = useState();
  const [error, setError] = useState();
  const { user } = useAuth();

  const handleInputChange = (e) => {
    for (let i = 0; i < e.target.value.length; i++) {
      setImages((prevState) => [...prevState, e.target.files[i]]);
    }
  };

  const handleUploadImages = (path) => {
    const promises = [];
    images.forEach((image) => {
      const imagesRef = ref(storage, `${user.email}/${path}/${image.name}`);
      const metadata = {
        contentType: "image/jpeg",
      };
      setProgress(true);
      const uploadTask = uploadBytesResumable(imagesRef, image, metadata);
      promises.push(uploadTask);

      uploadTask.on(
        "state_changed",
        (snapshot) => {
          const progress =
            (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
          setProgress(progress);
        },
        (error) => setError(error.message),
        async () => {
          const url = await getDownloadURL(imagesRef);
          setUrls((prevState) => [...prevState, url]);
        }
      );
    });

    Promise.all(promises)
      .then(() => setProgress(false))
      .catch((error) => setError(error.message))
      .finally(() => {});
  };

  // console.log({ urls, images });
  return (
    <Container>
      <Card className="d-flex flex-row justify-content-center align-items-center">
        <Card.Body style={{ maxWidth: "400px" }}>
          <Card.Header as="h1" className="mb-4">
            Homepage
          </Card.Header>

          {error && <Alert variant="danger">{error}</Alert>}

          {progress && (
            <ProgressBar
              now={progress}
              max="100"
              label={`Loading...${progress}%`}
            />
          )}

          <Form>
            <Form.Group>
              <Form.Label htmlFor="addImgs">Add Images</Form.Label>
              <Form.Control
                multiple
                id="addImgs"
                type="file"
                onChange={(e) => handleInputChange(e)}
              />
              <Button onClick={handleUploadImages("Images")}>Upload</Button>
            </Form.Group>

            <Form.Group>
              <Row>
                {urls.map((url, index) => (
                  <Col xs={6} key={url + index} style={{ listStyle: "none" }}>
                    <Image
                      fluid
                      src={url}
                      alt={url}
                      style={{ height: "50vw" }}
                    />
                  </Col>
                ))}
              </Row>
            </Form.Group>
          </Form>
        </Card.Body>
      </Card>
    </Container>
  );
}
