import React from "react";

export const AddTodoForm = ({ onAddTodoSubmit, onAddTodoInput }) => (
  <form
    onSubmit={onAddTodoSubmit}
    className="my-3 d-flex justify-content-start align-items-center"
  >
    <label
      htmlFor="addForm"
      style={{
        fontSize: "20px",
        fontWeight: "600",
        
        lineHeight: "1",
      }}
    >
      Add your work:
    </label>

    <input
      id="addForm"
      type="text"
      placeholder="What do you want to do?"
      onChange={onAddTodoInput}
      autoFocus
    />

    <button className="btn btn-primary" type="submit">
      Add
    </button>
  </form>
);
