import { ReactComponent as Check } from "../svg/checked-mark.svg";
import * as SC from "../style";
import React from "react";

export const List = React.memo(({ list, onRemoveItem }) => (
  <div>
    <ul>
      <SC.StyledItem>
        <SC.StyledButtonSmall style={{ width: "40%", paddingRight: "-10px" }}>Title</SC.StyledButtonSmall>
        <SC.StyledButtonSmall style={{ width: "20%", paddingRight: "-10px" }}>Author</SC.StyledButtonSmall>
        <SC.StyledButtonSmall style={{ width: "20%", paddingRight: "-10px" }}>Comments</SC.StyledButtonSmall>
        <SC.StyledButtonSmall style={{ width: "10%", paddingRight: "-10px" }}>Points</SC.StyledButtonSmall>
        <SC.StyledButtonSmall style={{ width: "10%", paddingRight: "-10px" }}>Actions</SC.StyledButtonSmall>
      </SC.StyledItem>
    </ul>
    <ul>
      {list.map((item) => (
        <Item key={item.objectID} item={item} onRemoveItem={onRemoveItem} />
      ))}
    </ul>
  </div>
));

export const Item = React.memo(({ item, onRemoveItem }) => (
  <SC.StyledItem>
    <SC.StyledColumn width="40%">
      <a href={item.url}>{item.title}</a>
    </SC.StyledColumn>
    <SC.StyledColumn width="20%">{item.author}</SC.StyledColumn>
    <SC.StyledColumn width="20%">{item.num_comments}</SC.StyledColumn>
    <SC.StyledColumn width="10%">{item.points}</SC.StyledColumn>

    <SC.StyledColumn width="10%">
      <SC.StyledButtonSmall type="button" onClick={() => onRemoveItem(item)}>
        <Check height="10px" width="10px" />
      </SC.StyledButtonSmall>
    </SC.StyledColumn>
  </SC.StyledItem>
));
