import { Fragment } from "react";
import { useEffect, useState, useRef } from "react";
import "./App.css";
//Custom HooksetValue
const usePersistentState = (key, initialState) => {
  const [value, setValue] = useState(localStorage.getItem(key) || initialState);

  useEffect(() => {
    localStorage.setItem(key, value);
  }, [key, value]);

  return [value, setValue];
};

const App = () => {
  const stories = [
    {
      title: "React",
      url: "https://reactjs.org/",
      author: "Jordan Walke",
      num_comments: 3,
      points: 4,
      objectID: 0,
    },
    {
      title: "Redux",
      url: "https://redux.js.org/",
      author: "Dan Abramov, Andrew Clark",
      num_comments: 2,
      points: 5,
      objectID: 1,
    },
  ];

  const [searchTerm, setSearchTerm] = usePersistentState("search", "Redux");

  const handleChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const searchedStories = stories.filter((story) => {
    return story.title.toLowerCase().includes(searchTerm.toLowerCase());
  });

  return (
    <div>
      <h1>My React AponInputChangep</h1>
      <InputWithLabel
        onInputChange={handleChange}
        value={searchTerm}
        id="search"
        label="Search"
        isFocused={true} // or isFocused =  (isFocused = {true})
      >
        <StringChildren text="Search for:" />
      </InputWithLabel>

      <List list={searchedStories} />
    </div>
  );
};

export default App;

const InputWithLabel = ({
  isFocused,
  children,
  type = "text",
  id,
  value,
  onInputChange,
}) => {
  const inputRef = useRef();

  useEffect(() => {
    if (isFocused) {
      inputRef.current.focus();
    }
  }, [isFocused]);

  return (
    <>
      <label htmlFor={id}>{children}</label>
      &nbsp;
      <input
        ref={inputRef}
        id={id}
        type={type}
        value={value}
        onChange={onInputChange}
      />
    </>
  );
};

const StringChildren = ({ text }) => {
  return <strong>{text}</strong>;
};

const List = ({ list }) => (
  <ul>
    {list.map(({ objectID, ...item }) => (
      <Item key={objectID} {...item} />
    ))}
  </ul>
);

const Item = ({ title, author, url, num_comments, points }) => (
  <li>
    <a href={url}>{title}: </a>
    <span>{author} </span>
    <p>Comments: {num_comments}</p>
    <p>Points: {points}</p>
  </li>
);
