import React, { useEffect, useReducer, useState } from "react";
import axios from "axios";

const todoReducer = (state, action) => {
  switch (action.type) {
    case "TODOS_FETCH_INIT":
      return {
        ...state,
        isLoading: true,
        isError: false,
      };
    case "TODOS_FETCH_SUCCESS":
      return {
        ...state,
        isLoading: false,
        isError: false,
        data: action.payload,
      };
    case "TODOS_FETCH_FAILURE":
      return {
        ...state,
        isLoading: false,
        isError: true,
      };
    case "DO_TODO":
      return {
        ...state,
        data: state.data.map((todo) => {
          if (todo.id === action.id) {
            return { ...todo, completed: true };
          } else {
            return todo;
          }
        }),
      };
    case "UNDO_TODO":
      return {
        ...state,
        data: state.data.map((todo) => {
          if (todo.id === action.id) {
            return { ...todo, completed: false };
          } else {
            return todo;
          }
        }),
      };
    case "REMOVE_TODO":
      return {
        ...state,
        data: state.data.filter((todo) => todo.id !== action.payload.id),
      };

    case "ADD_TODO":
      return {
        ...state,
        data: state.data.concat({
          id: Math.random(),
          title: action.payload,
          completed: false,
        }),
      };
    case "MARK_ALL_TODOS":
      return {
        ...state,
        data: state.data.map((todo) => {
          return {
            ...todo,
            completed: true,
          };
        }),
      };

    default:
      return state;
  }
};

const TodoApp = () => {
  const [todos, dispatchTodos] = useReducer(todoReducer, {
    data: [],
    isLoading: false,
    isError: false,
  });

  const [todoQuery, setTodoQuery] = useState("");

  const handleFetchTodos = React.useCallback(async () => {
    dispatchTodos({
      type: "TODOS_FETCH_INIT",
    });

    try {
      const respone = await axios.get(
        `https://jsonplaceholder.typicode.com/todos`
      );
      setTimeout(
        () =>
          dispatchTodos({
            type: "TODOS_FETCH_SUCCESS",
            payload: respone.data,
          }),
        2000
      );
    } catch {
      dispatchTodos({
        type: "TODOS_FETCH_FAILURE",
      });
    }
  }, []);

  useEffect(() => handleFetchTodos(), [handleFetchTodos]);

  const handleCheckedTodo = (todo) => {
    dispatchTodos({
      type: todo.completed ? "UNDO_TODO" : "DO_TODO",
      id: todo.id,
    });
  };

  const handleRemoveTodo = (todo) => {
    dispatchTodos({
      type: "REMOVE_TODO",
      payload: todo,
    });
  };

  const handleAddTodo = (todoQuery) => {
    dispatchTodos({
      type: "ADD_TODO",
      payload: todoQuery,
    });
  };

  const handleAddTodoInput = (e) => {
    setTodoQuery(e.target.value);
  };

  const handleAddTodoSubmit = (e) => {
    e.preventDefault();
    handleAddTodo(todoQuery);
  };

  const handleMarkAllTodos = (list) => {
    dispatchTodos({
      type: "MARK_ALL_TODOS",
      payload: list,
    });
  };

  const handleCheckboxChange = (e) => {
    console.log('value of checkbox : ', e.target.checked);
  }

  const sum_todos = todos.data.length;
  const unDoneTodos = 0; /* todos.data.filter(todo => todo.completed === false) */

  return (
    <div>
      <h1
        style={{
          textAlign: "center",
          fontWeight: "700",
          fontSize: "48px",
        }}
      >
        My Todo with {sum_todos} works.
      </h1>
      <p>{unDoneTodos} Works uncompleted! </p>
      {todos.isError && <p>Oops! Something went wrong</p>}

      {todos.isLoading ? (
        <p>Loading...</p>
      ) : (
        <List
          list={todos.data}
          onRemoveTodo={handleRemoveTodo}
          onCheckedTodo={handleCheckedTodo}
          onMarkAllTodos={handleMarkAllTodos}
          handleCheckboxChange={handleCheckboxChange}
        />
      )}

      <AddTodoForm
        onAddTodoInput={handleAddTodoInput}
        onAddTodoSubmit={handleAddTodoSubmit}
      >
        Add your work:
      </AddTodoForm>
    </div>
  );
};
export default TodoApp;

const InputWithLabel = ({
  isFocused,
  checked,
  value,
  type = "text",
  id,
  children,
  onInputChange,
}) => {
  const inputRef = React.useRef(null);
  if (isFocused && inputRef.current) {
    inputRef.current.focus();
  }

  return (
    <>
      <label htmlFor={id}>
        {children}
        &nbsp;
        <input
          value={value}
          id={id}
          type={type}
          onChange={onInputChange}
          checked={checked}
          ref={inputRef}
        />
      </label>
    </>
  );
};

const AddTodoForm = ({ onAddTodoSubmit, onAddTodoInput }) => (
  <form onSubmit={onAddTodoSubmit}>
    <InputWithLabel onInputChange={onAddTodoInput} id="add" isFocused={false}>
      Add your work:
    </InputWithLabel>
    <button type="submit"> Add </button>
  </form>
);

const List = ({
  list,
  onRemoveTodo,
  onCheckedTodo,
  onMarkAllTodos,
  onUnMarkAllTodos,
}) => (
  <>
    <ul>
      {list.map((todo) => (
        <Item
          todo={todo}
          onRemoveTodo={onRemoveTodo}
          onCheckedTodo={onCheckedTodo}
          key={todo.id}
        />
      ))}
    </ul>
    <div>
      <label htmlFor="markAllTodos">
        <input
          id="markAllTodos"
          type="checkbox"
          onChange={handleCheckboxChange}
        />
  
        <span>Mark all works</span>
      </label>
    </div>
  </>
);

const Item = ({ todo, onRemoveTodo, onCheckedTodo }) => {
  return (
    <li>
      <button
        style={{
          padding: "5px",
          marginRight: "5px",
        }}
        onClick={() => onRemoveTodo(todo)}
      >
        Delete
      </button>

      <InputWithLabel
        id={todo.id}
        value={todo.title}
        checked={todo.completed}
        type="checkbox"
        onInputChange={() => onCheckedTodo(todo)}
      >
        <span>{todo.title}</span>
      </InputWithLabel>
    </li>
  );
};
