import { db } from "./config";
import {
  collection,
  addDoc,
  getDocs,
  serverTimestamp,
} from "firebase/firestore";

export const addDocuments = (collections, data) => {
  addDoc(collection(db, collections), {
    ...data,
    createdAt: serverTimestamp(),
  });
};
