import React from "react";
import { BrowserRouter as Router, Route, Link, Switch } from "react-router-dom";
import TodoApp from "./TodoApp";
import HackerNewStories from "./HackerNewStoriesApp";
import Home from "./Home";
import Login from "./Login";
import SignUp from "./SignUp";
import PrivateRoute from "./PrivateRoute";
import "./FontAwesome";
import "./App.css";
import ScrollTopArrow from "./ScrollTopArrow";
import { Navbar } from "react-bootstrap";
import { AuthProvider } from "../contexts/AuthContext";
import { createBrowserHistory } from "history";

function ListItemLink({ to, ...rest }) {
  return (
    <Route
      path={to}
      children={({ match }) => (
        <li className={match ? "active" : ""}>
          <Link to={to} {...rest} />
        </li>
      )}
    />
  );
}

export const App = () => {
  return (
    <Router history={history}>
      <Navbar className="d-flex justify-content-end ">
        <ul className="nav">
          <li className="nav-item">
            <Link to="/" style={{ textDecoration: "none" }}>
              <span className="nav-link">Home</span>
            </Link>
          </li>
          <li className="nav-item ">
            <Link to="/login" style={{ textDecoration: "none" }}>
              <span className="nav-link">Login</span>
            </Link>
          </li>
          <li className="nav-item">
            <Link to="/signup" style={{ textDecoration: "none" }}>
              <span className="nav-link">Sign Up</span>
            </Link>
          </li>
          <li className="nav-item">
            <Link to="/todo-app" style={{ textDecoration: "none" }}>
              <span className="nav-link">Todo App</span>
            </Link>
          </li>
          <li className="nav-item">
            <Link to="/stories-app" style={{ textDecoration: "none" }}>
              <span className="nav-link">Hacker News Stories App</span>
            </Link>
          </li>
          <ListItemLink to="/todo-app" />
        </ul>
      </Navbar>

      <AuthProvider>
        <Switch>
          <Route path="/todo-app" component={TodoApp} />
          <Route path="/stories-app" component={HackerNewStories} />
          <PrivateRoute exact path="/" component={Home} />
          <Route path="/login" component={Login} />
          <Route path="/signup" component={SignUp} />
        </Switch>
      </AuthProvider>

      <ScrollTopArrow />
    </Router>
  );
};

export default App;
export const history = createBrowserHistory();
