const App = () => {
  const stories = [
    {
      title: "React",
      url: "https://reactjs.org/",
      author: "Jordan Walke",
      num_comments: 3,
      points: 4,
      objectID: 0,
    },
    {
      title: "Redux",
      url: "https://redux.js.org/",
      author: "Dan Abramov, Andrew Clark",
      num_comments: 2,
      points: 5,
      objectID: 1,
    },
  ];
  return (
    <div>
      <h1>My React App</h1>
      <label htmlFor="search">Search:</label>
      <input id="search" type="text" />

      <List lsit={stories}/>
    </div>
  );
};

export default App;

const List = (props) => {
  <ul>
    {props.list.map((item) => (
      <li key={item.objectID}>
        <a href={item.url}>{item.title}:</a>
        <span> {item.author}</span>
      </li>
    ))}
  </ul>;
};
