import React, { useState } from "react";
import { Button, Alert, Card, Modal, Form, ProgressBar } from "react-bootstrap";

import { useAuth } from "../../contexts/AuthContext";
import { Prompt } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { storage } from "../../firebase/config";
import {
  ref,
  uploadBytesResumable,
  getDownloadURL,
  deleteObject,
} from "firebase/storage";

/* import { v1 as uuidv1 } from "uuid"; */

export const Homepage = () => {
  const { user } = useAuth();
  const [error, setError] = useState("");
  const [show, setShow] = useState(false);
  const [isBlocking, setIsBlocking] = useState(false);

  const [fileUpload, setFileUpload] = useState(null);
  const [fileUrl, setFileUrl] = useState("");
  const [progress, setProgress] = useState();

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const handleUploadFile = (path, file) => {
    if (file) {
      const imageRef = ref(storage, `${user.email}/${path}/${file.name}`);

      const metadata = {
        contentType: "image/jpeg",
      };
      const uploadTask = uploadBytesResumable(imageRef, file, metadata);

      uploadTask.on(
        "state_changed",
        (snapshot) => {
          const progress =
            (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
          setProgress(progress);
        },
        (error) => setError(error.message),
        async () => {
          try {
            const url = await getDownloadURL(imageRef);
            setFileUrl(url);
            setProgress(null);
          } catch (error) {
            setError(error.message);
          }
        }
      );
    }
  };

  const handleInputChange = (e) => {
    if (e.target.files[0]) {
      handleUploadFile("Images-upload", e.target.files[0]);
      setFileUpload(e.target.files[0]);
    }
  };

  const handleCancelUploadFile = (path, file) => {
    if (file) {
      const imagesRef = ref(storage, `${user.email}/${path}/${file.name}`);
      deleteObject(imagesRef);
      setFileUrl("");
      setFileUpload(null);
    }
  };

  return (
    <Card className="d-flex flex-row justify-content-center align-items-center">
      <Card.Body style={{ maxWidth: "400px" }}>
        <Card.Header as="h1" className="mb-4">
          Homepage
        </Card.Header>

        {error && <Alert variant="danger">{error}</Alert>}

        <Prompt
          when={isBlocking}
          message="Seems you are not finishing your works. Do you want to go?"
        />

        <Button onClick={handleShow}>Add images</Button>
        <Card.Img fluid src={fileUrl} />

        <div className="mb-3  d-flex justify-content-center">
          <Modal show={show} onHide={handleClose}>
            <Modal.Body>
              <Form>
                <Form.Group>
                  <Form.Label htmlFor="photo">Select a photo</Form.Label>
                  <Form.Control
                    id="photo"
                    type="file"
                    onChange={(e) => {
                      setIsBlocking(e.target.value.length > 0);
                      handleInputChange(e);
                    }}
                    accept=".jpg, .jpeg, .png"
                  ></Form.Control>

                  {fileUrl && (
                    <div className="d-flex flex-column justify-content-center align-items-center">
                      <div style={{ position: "relative" }}>
                        <Card.Img
                          variant="top"
                          src={fileUrl}
                          alt="Avatar"
                          className="my-3"
                          fluid
                          style={{
                            borderRadius: "10px",
                          }}
                        />

                        <FontAwesomeIcon
                          className="closed-icon"
                          style={{
                            position: "absolute",
                            top: "1.25rem",
                            right: "0.75rem",
                            cursor: "pointer",
                          }}
                          icon={["fas", "times"]}
                          size="lg"
                          onClick={() =>
                            handleCancelUploadFile("Images-upload", fileUpload)
                          }
                        />
                      </div>

                      {progress && (
                        <ProgressBar
                          animated
                          now={progress}
                          label={`Loading...${progress}%`}
                          visibly-hidden
                          max="100"
                          className="w-75 mb-3"
                        />
                      )}
                    </div>
                  )}
                </Form.Group>
              </Form>
            </Modal.Body>

            <Modal.Footer>
              <Button variant="secondary" type="button" onClick={handleClose}>
                Close
              </Button>
              <Button
                type="button"
                variant="primary"
                onClick={handleUploadFile("Images", fileUpload)}
              >
                Save Changes
              </Button>
            </Modal.Footer>
          </Modal>
        </div>
      </Card.Body>
    </Card>
  );
};

export default Homepage;
