import React from "react";
import { useAuth } from "../../contexts/AuthContext";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { Card } from "react-bootstrap";

export default function Avatar({handleShow}) {
  const { user } = useAuth();
  return (
    <>
      {user.photoURL ? (
        <div style={{ position: "relative" }}>
          <Card.Img
            variant="top"
            src={user.photoURL}
            alt="Avatar"
            style={{
              width: "100px",
              height: "100px",
              borderRadius: "50%",
            }}
          />
          <FontAwesomeIcon
            icon={["fas", "camera"]}
            style={{
              position: "absolute",
              bottom: "0",
              right: "0",
              cursor: "pointer",
            }}
            onClick={handleShow}
          />
        </div>
      ) : (
        <div
          style={{
            width: "100px",
            height: "100px",
            border: "1px solid #0B3D6B",
            borderRadius: "50%",
            background: "#0B3D6B",
          }}
          className="text-white d-flex justify-content-center align-items-center"
        >
          <span style={{ fontSize: "30px", fontWeight: "600" }}>
            {user.displayName?.charAt(0)}
          </span>
          <FontAwesomeIcon
            icon={["fas", "camera"]}
            style={{
              position: "absolute",
              bottom: "0",
              right: "0",
              cursor: "pointer",
            }}
            onClick={handleShow}
          />
        </div>
      )}
    </>
  );
}
