import React, { useEffect, useReducer, useState, useCallback } from "react";
import { List } from "./List";
import { AddTodoForm } from "./AddForm";
import { todoReducer } from "./reducer";
import axios from "axios";
import { Link } from "react-router-dom";
import * as SC from "./style";

const API_ENDPOINT = "https://jsonplaceholder.typicode.com/todos";
const PARAM_SEARCH = "_page=";
const PARAM_USERSEARCH = "userId=";

const getUrl = (page, searchId) =>
  `${API_ENDPOINT}?${PARAM_SEARCH}${page}&${PARAM_USERSEARCH}${searchId}`;

const TodoApp = () => {
  const [todos, dispatchTodos] = useReducer(todoReducer, {
    data: [],
    isLoading: false,
    isError: false,
  });

  const sum_todos = todos.data.length;
  const [unDoneTodos, setUnDoneTodos] = useState(0);
  const [todoQuery, setTodoQuery] = useState("");
  const [page, setPage] = useState(1);
  const [searchId, setSearchId] = useState(1);
  const [url, setUrl] = useState(getUrl(page, searchId));

  const handleFetchTodos = useCallback(async () => {
    dispatchTodos({
      type: "TODOS_FETCH_INIT",
    });

    try {
      const respone = await axios.get(url);
      dispatchTodos({
        type: "TODOS_FETCH_SUCCESS",
        payload: {
          list: respone.data,
          page: page,
        },
      });
    } catch {
      dispatchTodos({
        type: "TODOS_FETCH_FAILURE",
      });
    }
  }, [url, page]);

  useEffect(() => handleFetchTodos(), [handleFetchTodos]);

  const handleMore = () => {
    setPage(page + 1);
    const url = getUrl(page + 1, searchId);
    setUrl(url);
  };

  useEffect(() => {
    setUnDoneTodos(
      todos.data.filter((todo) => todo.completed === false).length
    );
  }, [todos]);

  const handleCheckedTodo = (todo) => {
    dispatchTodos({
      type: todo.completed ? "UNDO_TODO" : "DO_TODO",
      id: todo.id,
    });
  };

  const handleRemoveTodo = (todo) => {
    dispatchTodos({
      type: "REMOVE_TODO",
      payload: todo,
    });
  };

  const handleAddTodo = (todoQuery) => {
    dispatchTodos({
      type: "ADD_TODO",
      payload: todoQuery,
    });
  };

  const handleAddTodoInput = (e) => {
    setTodoQuery(e.target.value);
  };

  const handleAddTodoSubmit = (e) => {
    e.preventDefault();
    handleAddTodo(todoQuery);
  };

  const handleCheckboxChange = (list, e) =>
    dispatchTodos({
      type: e.target.checked ? "MARK_ALL_TODOS" : "UNMARK_ALL_TODOS",
      payload: list,
    });

  const handleSearchTerm = (e) => {
    setSearchId(e.target.value);
  };

  const handleSearchSubmit = () => {
    const url = getUrl(page, searchId);
    setUrl(url);
  };

  return (
    <div>
      <nav className="d-flex justify-content-end">
        <ul className="nav">
          <li className="nav-item">
            <Link to="/">
              <span className="nav-link">Home</span>
            </Link>
          </li>
          <li className="nav-item">
            <Link to="/todo-app">
              <span className="nav-link">Todo App</span>
            </Link>
          </li>
          <li className="nav-item">
            <Link to="/stories-app">
              <span className="nav-link">Hacker News Stories App</span>
            </Link>
          </li>
        </ul>
      </nav>

      <h1>My Todo with {sum_todos} works.</h1>

      <SC.StyledMessage>{unDoneTodos} Works uncompleted!</SC.StyledMessage>

      {todos.isError && (
        <SC.StyledMessage>Oops! Something went wrong</SC.StyledMessage>
      )}

      <List
        list={todos.data}
        onRemoveTodo={handleRemoveTodo}
        onCheckedTodo={handleCheckedTodo}
        onCheckboxChange={handleCheckboxChange}
      />

      {todos.isLoading ? (
        <SC.StyledMessage>Loading...</SC.StyledMessage>
      ) : (
        <button className="btn btn-primary" onClick={handleMore}>
          More
        </button>
      )}

      <SC.StyledForm className="form mb-3" onSubmit={handleSearchSubmit}>
        <SC.StyledLabel className="form-group" htmlFor="IdSearch">
          Search by UserId
        </SC.StyledLabel>
        <input
          id="IdSearch"
          className="form-group form-control"
          type="text"
          placeholder="Your UserId"
          onChange={handleSearchTerm}
        />
      </SC.StyledForm>

      <AddTodoForm
        onAddTodoInput={handleAddTodoInput}
        onAddTodoSubmit={handleAddTodoSubmit}
      />
    </div>
  );
};
export default TodoApp;
