import React, { useEffect, useReducer, useState } from "react";
import { InputWithLabel } from "./App";
import axios from "axios";

const todoReducer = (state, action) => {
  switch (action.type) {
    case "SET_TODOS":
      return action.payload;
    case "DO_TODO":
      return state.map((todo) => {
        if (todo.id === action.id) {
          return { ...todo, complete: true };
        } else {
          return todo;
        }
      });
    case "UNDO_TODO":
      return state.map((todo) => {
        if (todo.id === action.id) {
          return { ...todo, complete: false };
        } else {
          return todo;
        }
      });
    case "REMOVE_TODO":
      return state.filter((todo) => todo.id !== action.payload.id);
    default:
      return state;
    case "ADD_TODO":
      return state.concat({
        id: Math.random(),
        title: action.payload,
        complete: false,
      });
  }
};

const TodoApp = () => {
  const initialTodos = [
    {
      userId: 1,
      id: 1,
      title: "delectus aut autem",
      completed: false,
    },
    {
      userId: 1,
      id: 2,
      title: "quis ut nam facilis et officia qui",
      completed: false,
    },
    {
      userId: 1,
      id: 3,
      title: "fugiat veniam minus",
      completed: false,
    },
    {
      userId: 1,
      id: 4,
      title: "et porro tempora",
      completed: true,
    },
    {
      userId: 1,
      id: 5,
      title: "laboriosam mollitia et enim quasi adipisci quia provident illum",
      completed: false,
    },
    {
      userId: 1,
      id: 6,
      title: "qui ullam ratione quibusdam voluptatem quia omnis",
      completed: false,
    },
    {
      userId: 1,
      id: 7,
      title: "illo expedita consequatur quia in",
      completed: false,
    },
    {
      userId: 1,
      id: 8,
      title: "quo adipisci enim quam ut ab",
      completed: true,
    },
    {
      userId: 1,
      id: 9,
      title: "molestiae perspiciatis ipsa",
      completed: false,
    },
    {
      userId: 1,
      id: 10,
      title: "illo est ratione doloremque quia maiores aut",
      completed: true,
    }
  ];
  const [todos, dispatchTodos] = useReducer(todoReducer, []);

  const handleTodoStatus = (todo) => {
    dispatchTodos({
      type: todo.complete ? "UNDO_TODO" : "DO_TODO",
      id: todo.id,
    });
  };

  const handleRemoveTodo = (todo) => {
    dispatchTodos({
      type: "REMOVE_TODO",
      payload: todo,
    });
  };

  const [todoQuery, setTodoQuery] = useState("");

  const handleTodoQuery = (e) => {
    setTodoQuery(e.target.value);
  };

  const handleAddTodo = (todoQuery, e) => {
    dispatchTodos({
      type: "ADD_TODO",
      payload: todoQuery,
    });
  };

  const onSubmitTodo = (e) => {
    e.preventDefault();
    handleAddTodo(todoQuery);
  };

  useEffect(() => {
    const fetchData = async () => {
      const respone = await axios.get(`/initialTodos`);
      dispatchTodos({
        type: "SET_TODOS",
        payload: respone.data,
      });
    };
    fetchData();
  }, []);
  return (
    <div>
      <h1> Todo List </h1>

      <ul>
        {todos.map((todo) => (
          <li key={todo.id}>
            <button onClick={() => handleRemoveTodo(todo)}> Delete </button>
            &nbsp;
            <InputWithLabel
              checked={todo.completed}
              type="checkbox"
              onInputChange={() => handleTodoStatus(todo)}
              value={todo.title}

            >
              {todo.title}
            </InputWithLabel>
          </li>
        ))}
      </ul>
      <form onSubmit={onSubmitTodo}>
        <InputWithLabel type="text" onInputChange={handleTodoQuery}>
          Add Todo:
        </InputWithLabel>
        <button type="submit"> Add </button>
      </form>
    </div>
  );
};

export default TodoApp;
