import React from "react";
import axios from "axios";
import {
  useCallback,
  useMemo,
  useReducer,
  useEffect,
  useState,
  useRef,
} from "react";
import * as SC from "./style";
/* import TodoApp from "./TodoApp"; */
import { SearchForm } from "./SearchForm";
import { List } from "./List";
import { LastSearches } from "./LastSearches";
import { usePersistentState } from "./hooks/Custom Hook";

//Reducer
//Stories reducer
const storiesReducer = (state, action) => {
  switch (action.type) {
    case "STORIES_FETCH_INIT":
      return {
        ...state,
        isLoading: true,
        isError: false,
      };
    case "STORIES_FETCH_SUCCESS":
      return {
        ...state,
        isLoading: false,
        isError: false,
        data: action.payload,
      };
    case "STORIES_FETCH_FAILURE":
      return {
        ...state,
        isLoading: false,
        isError: true,
      };
    case "REMOVE_STORY":
      return {
        ...state,
        data: state.data.filter(
          (story) => story.objectID !== action.payload.objectID
        ),
      };
    default:
      throw new Error();
  }
};

const API_ENDPOINT = "https://hn.algolia.com/api/v1/search?query=";
const getUrl = (searchTerm) => `${API_ENDPOINT}${searchTerm}`;

const App = () => {
  const [stories, dispatchStories] = useReducer(storiesReducer, {
    data: [],
    isLoading: false,
    isError: false,
  });
  const [searchTerm, setSearchTerm] = usePersistentState("search", "React");
  const [urls, setUrls] = useState([getUrl(searchTerm)]);
  const extractSearchTerm = (url) => url.replace(API_ENDPOINT, "");
  const getLastSearches = (urls) =>
    urls
      .reduce((result, url, index) => {
        const searchTerm = extractSearchTerm(url);
        if (index === 0) {
          return result.concat(searchTerm);
        }
        const previousSearchTerm = result[result.length - 1];
        if (previousSearchTerm === searchTerm) {
          return result;
        } else {
          return result.concat(searchTerm);
        }
      }, [])
      .slice(-6)
      .slice(0, -1);

  const lastSearchs = getLastSearches(urls);

  const handleFetchStories = useCallback(async () => {
    dispatchStories({
      type: "STORIES_FETCH_INIT",
    });

    try {
      const lastUrl = urls[urls.length - 1];
      const respone = await axios.get(lastUrl);
      dispatchStories({
        type: "STORIES_FETCH_SUCCESS",
        payload: respone.data.hits,
      });
    } catch {
      dispatchStories({
        type: "STORIES_FETCH_FAILURE",
      });
    }
  }, [urls]);

  useEffect(() => {
    handleFetchStories();
  }, [handleFetchStories]);

  const handleRemoveStory = useCallback((item) => {
    dispatchStories({
      type: "REMOVE_STORY",
      payload: item,
    });
  }, []);

  const handleSearchInput = useCallback(
    (e) => setSearchTerm(e.target.value),
    [setSearchTerm]
  );

  const handleSearchSubmit = (e) => {
    handleSearch(searchTerm);
    e.preventDefault();
  };

  const handleLastSearch = (searchTerm) => {
    handleSearch(searchTerm);
    setSearchTerm(searchTerm);
  };

  const handleSearch = useCallback(
    (searchTerm) => {
      const url = getUrl(searchTerm);
      setUrls(urls.concat(url));
    },
    [urls]
  );

  const getSumComments = (stories) => {
    return stories.data.reduce(
      (result, value) => result + value.num_comments,
      0
    );
  };

  const sumComments = useMemo(() => getSumComments(stories), [stories]);

  return (
    <SC.StyledContainer>
      {/*  <TodoApp /> */}

      <SC.StyledHeadlinePrimary>
        My Hacker Stories with {sumComments} comments
      </SC.StyledHeadlinePrimary>

      <SearchForm
        searchTerm={searchTerm}
        onSearchSubmit={handleSearchSubmit}
        onSearchInput={handleSearchInput}
      />

      <LastSearches lastSearchs={lastSearchs} onLastSearch={handleLastSearch} />

      {stories.isError && (
        <p>
          <strong>Oops! Something went wrong...</strong>
        </p>
      )}

      {stories.isLoading ? (
        <p>
          <strong>Loading...</strong>
        </p>
      ) : (
        <List list={stories.data} onRemoveItem={handleRemoveStory} />
      )}
    </SC.StyledContainer>
  );
};

export default App;
