import React, { useState } from "react";
import { Modal, Button } from "react-bootstrap";
import ReactDOM from "react-dom";

export default function UserLeaveConfirm(
  message,
  callback,
  confirmShow,
  setConfirmShow
) {
  const container = ReactDOM.createElement("div");
  container.setAttribute("custom-promp", "");

  const handleConfirm = (callbackState) => {
    ReactDOM.unmountComponentAtNode(container);
    callback(callbackState);
    setConfirmShow(false);
  };

  const handleCancel = (callbackState) => {
    ReactDOM.unmountComponentAtNode(container);
    callback();
    setConfirmShow(false);
  };

  document.body.appendChild(container);

  ReactDOM.render(
    <div>
      <Modal show={confirmShow} onHide={handleCancel}>
        <Modal.Header closeButton>
          <Modal.Title>Modal heading</Modal.Title>
        </Modal.Header>
        <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCancel}>
            Close
          </Button>
          <Button variant="primary" onClick={handleConfirm}>
            Ok
          </Button>
        </Modal.Footer>
      </Modal>
    </div>,
    container
  );
}
