import React from "react";
import { sortBy } from "lodash";
import { InputWithLabel } from "./InputWithLabel";

const SORTS = {
  NONE: (list) => list,
  TITLE: (list) => sortBy(list, "title"),
};

export const List = ({
  list,
  onRemoveTodo,
  onCheckedTodo,
  onCheckboxChange,
}) => {
  const [sort, setSort] = React.useState({
    sortKey: "NONE",
    isReversed: false,
  });
  const handleSort = (sortKey) => {
    const isReversed = sort.sortKey === sortKey && !sort.isReversed;
    setSort({ sortKey, isReversed });
  };

  const sortFunction = SORTS[sort.sortKey];
  const sortedList = sort.isReversed
    ? sortFunction(list).reverse()
    : sortFunction(list);

  return (
    <div>
      <div>
        <label htmlFor="markAllTodos">
          <input
            id="markAllTodos"
            type="checkbox"
            onChange={(e) => onCheckboxChange(list, e)}
            autoFocus={true}
          />

          <span>Mark all works</span>
        </label>
      </div>

      <div>
        <button type="button" onClick={() => handleSort("TITLE")}>
          Sort List
        </button>
      </div>

      <div style={{ listStyle: "none" }}>
        {sortedList.map((todo) => (
          <Item
            todo={todo}
            onRemoveTodo={onRemoveTodo}
            onCheckedTodo={onCheckedTodo}
            key={todo.id}
          />
        ))}
      </div>
    </div>
  );
};

const Item = ({
  todo,
  onRemoveTodo,
  onCheckedTodo,
  isFocused,
  type = "checkbox",
  id="check",
}) => {
  const inputRef = React.useRef(null);
  if (isFocused && inputRef.current) {
    inputRef.current.focus();
  }
  return (
    <div style={{ display: "flex", margin: "10px" }}>
      <button
        style={{
          padding: "5px",
          margin: "5px",
          width: "10%",
        }}
        onClick={() => onRemoveTodo(todo)}
      >
        Delete
      </button>

      <div style={{ display: "flex", width: "90%" }}>
        <label
          style={{ width: "70%", margin: "10px", textTransform: "capitalize" }}
          htmlFor={id}
        >
          <span
            style={{
              whiteSpace: "nowrap",
              overFlow: "hidden",
              textOverflow: "ellipsis",
            }}
          >
            {todo.title}
          </span>
        </label>
        &nbsp;
        <input
          style={{ width: "30%" }}
          value={todo.title}
          id={id}
          type={type}
          onChange={() => onCheckedTodo(todo)}
          checked={todo.completed}
          ref={inputRef}
        />
      </div>

      {/*   <InputWithLabel
        id={todo.id}
        value={todo.title}
        checked={todo.completed}
        type="checkbox"
        onInputChange={() => onCheckedTodo(todo)}
      >
        <span
          style={{
            whiteSpace: "nowrap",
            overFlow: "hidden",
            textOverflow: "ellipsis",
          }}
        >
          {todo.title}
        </span>
      </InputWithLabel> */}
    </div>
  );
};
