import React, { useEffect, useReducer, useState } from "react";
import { InputWithLabel } from "./App";
import axios from "axios";

const todoReducer = (state, action) => {
  switch (action.type) {
    case "SET_TODOS":
      return action.payload;
    case "DO_TODO":
      return state.map((todo) => {
        if (todo.id === action.id) {
          return { ...todo, completed: true };
        } else {
          return todo;
        }
      });
    case "UNDO_TODO":
      return state.map((todo) => {
        if (todo.id === action.id) {
          return { ...todo, completed: false };
        } else {
          return todo;
        }
      });
    case "REMOVE_TODO":
      return state.filter((todo) => todo.id !== action.payload.id);
    default:
      return state;
    case "ADD_TODO":
      return state.concat({
        id: Math.random(),
        title: action.payload,
        completed: false,
      });
  }
};

const TodoApp = () => {
  const initialTodos = [];
  const [todos, dispatchTodos] = useReducer(todoReducer, initialTodos);
  const [isError, setIsError] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  const handleTodoStatus = (todo) => {
    dispatchTodos({
      type: todo.completed ? "UNDO_TODO" : "DO_TODO",
      id: todo.id,
    });
  };

  const handleRemoveTodo = (todo) => {
    dispatchTodos({
      type: "REMOVE_TODO",
      payload: todo,
    });
  };

  const [todoQuery, setTodoQuery] = useState("");

  const handleTodoQuery = (e) => {
    setTodoQuery(e.target.value);
  };

  const handleAddTodo = (todoQuery) => {
    dispatchTodos({
      type: "ADD_TODO",
      payload: todoQuery,
    });
  };

  const onSubmitTodo = (e) => {
    e.preventDefault();
    handleAddTodo(todoQuery);
  };

  useEffect(() => {
    setIsLoading(true);
    const fetchData = async () => {
      try {
        const respone = await axios.get(
          `https://jsonplaceholder.typicode.com/todoss`
        );
        dispatchTodos({
          type: "SET_TODOS",
          payload: respone.data,
        });
        setIsLoading(false);
      }
      catch{setIsError(true)}
    };
    fetchData();
    
  }, []);

  return (
    <div>
      <h1> Todo List </h1>
      {isError && <p>Oops! Something went wrong</p>}

      {isLoading ? (
        <p>Loading...</p>
      ) : (
        <ul>
          {todos.map((todo) => (
            <li key={todo.id}>
              <button onClick={() => handleRemoveTodo(todo)}> Delete </button>
              &nbsp;
              <InputWithLabel
                checked={todo.completed}
                type="checkbox"
                onInputChange={() => handleTodoStatus(todo)}
                value={todo.title}
              >
                {todo.title}
              </InputWithLabel>
            </li>
          ))}
        </ul>
      )}
      <form onSubmit={onSubmitTodo}>
        <InputWithLabel type="text" onInputChange={handleTodoQuery}>
          Add Todo:
        </InputWithLabel>
        <button type="submit"> Add </button>
      </form>
    </div>
  );
};

export default TodoApp;
