import React, { useEffect, useReducer, useState } from "react";
import { InputWithLabel } from "./App";
import axios from "axios";

const todoReducer = (state, action) => {
  switch (action.type) {
    case "TODO_FETCH_INIT":
      return {
        ...state,
        isLoading: true,
        isError: false,
      };
    case "TODO_FETCH_SUCCESS":
      return {
        ...state,
        isLoading: false,
        isError: false,
        data: action.payload,
      };
    case "TODO_FETCH_FAILURE":
      return {
        ...state,
        isLoading: false,
        isError: true,
      };
    case "DO_TODO":
      return {
        ...state,
        data: state.data.map((todo) => {
          if (todo.id === action.id) {
            return { ...todo, completed: true };
          } else {
            return todo;
          }
        }),
      };
    case "UNDO_TODO":
      return {
        ...state,
        data: state.data.map((todo) => {
          if (todo.id === action.id) {
            return { ...todo, completed: false };
          } else {
            return todo;
          }
        }),
      };
    case "REMOVE_TODO":
      return {
        ...state,
        data: state.data.filter((todo) => todo.id !== action.payload.id),
      };

    case "ADD_TODO":
      return {
        ...state,
        data: state.data.concat({
          id: Math.random(),
          title: action.payload,
          completed: false,
        }),
      };
    default:
      return state;
  }
};

const TodoApp = () => {
  const [todos, dispatchTodos] = useReducer(todoReducer, {
    data: [],
    isLoading: false,
    isError: false,
  });

  const handleTodoStatus = (todo) => {
    dispatchTodos({
      type: todo.completed ? "UNDO_TODO" : "DO_TODO",
      id: todo.id,
    });
  };

  const handleRemoveTodo = (todo) => {
    dispatchTodos({
      type: "REMOVE_TODO",
      payload: todo,
    });
  };

  const [todoQuery, setTodoQuery] = useState("");

  const handleTodoQuery = (e) => {
    setTodoQuery(e.target.value);
  };

  const handleAddTodo = (todoQuery) => {
    dispatchTodos({
      type: "ADD_TODO",
      payload: todoQuery,
    });
  };

  const onSubmitTodo = (e) => {
    e.preventDefault();
    handleAddTodo(todoQuery);
  };

  const handleFetchTodos = () => {
    dispatchTodos({
      type: "TODO_FETCH_INIT",
    });

    const fetchData = async () => {
      try {
        const respone = await axios.get(
          `https://jsonplaceholder.typicode.com/todos`
        );
        dispatchTodos({
          type: "TODO_FETCH_SUCCESS",
          payload: respone.data,
        });
      } catch {
        dispatchTodos({
          type: "TODO_FETCH_FAILURE",
        });
      }
    };
    fetchData();
  };

  useEffect(handleFetchTodos, [handleFetchTodos]);

  return (
    <div>
      <h1> Todo List </h1>
      {todos.isError && <p>Oops! Something went wrong</p>}

      {todos.isLoading ? (
        <p>Loading...</p>
      ) : (
        <ul>
          {todos.data.map((todo) => (
            <li key={todo.id}>
              <button onClick={() => handleRemoveTodo(todo)}> Delete </button>
              &nbsp;
              <InputWithLabel
                checked={todo.completed}
                type="checkbox"
                onInputChange={() => handleTodoStatus(todo)}
                value={todo.title}
              >
                {todo.title}
              </InputWithLabel>
            </li>
          ))}
        </ul>
      )}
      <form onSubmit={onSubmitTodo}>
        <InputWithLabel type="text" onInputChange={handleTodoQuery}>
          Add Todo:
        </InputWithLabel>
        <button type="submit"> Add </button>
      </form>
    </div>
  );
};

export default TodoApp;
