import axios from "axios";
import { Fragment, useCallback, useReducer } from "react";
import { useEffect, useState, useRef } from "react";
import "./App.css";
import TodoApp from "./TodoApp";

//Custom Hook
export const usePersistentState = (key, initialState) => {
  const [value, setValue] = useState(localStorage.getItem(key) || initialState);

  useEffect(() => {
    localStorage.setItem(key, value);
  }, [key, value]);

  return [value, setValue];
};

//Reducer
//Stories reducer
const storiesReducer = (state, action) => {
  switch (action.type) {
    case "STORIES_FETCH_INIT":
      return {
        ...state,
        isLoading: true,
        isError: false,
      };
    case "STORIES_FETCH_SUCCESS":
      return {
        ...state,
        isLoading: false,
        isError: false,
        data: action.payload,
      };
    case "STORIES_FETCH_FAILURE":
      return {
        ...state,
        isLoading: false,
        isError: true,
      };
    case "REMOVE_STORY":
      return {
        ...state,
        data: state.data.filter(
          (story) => story.objectID !== action.payload.objectID
        ),
      };
    default:
      throw new Error();
  }
};

const App = () => {
  const [stories, dispatchStories] = useReducer(storiesReducer, {
    data: [],
    isLoading: false,
    isError: false,
  });

  const [searchTerm, setSearchTerm] = usePersistentState("search", "React");
  const API_ENDPOINT = "https://hn.algolia.com/api/v1/search?query=";
  const [url, setUrl] = useState(`${API_ENDPOINT}${searchTerm}`);

  const handleFetchStories = useCallback(async () => {
    dispatchStories({
      type: "STORIES_FETCH_INIT",
    });

    try {
      const respone = await axios.get(url);
      dispatchStories({
        type: "STORIES_FETCH_SUCCESS",
        payload: respone.data.hits,
      });
    } catch {
      dispatchStories({
        type: "STORIES_FETCH_FAILURE",
      });
    }
  }, [url]);

  useEffect(() => {
    handleFetchStories();
  }, [handleFetchStories]);

  const handleRemoveStory = (item) => {
    dispatchStories({
      type: "REMOVE_STORY",
      payload: item,
    });
  };

  const handleSearchInput = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleSearchSubmit = (e) => {
    setUrl(`${API_ENDPOINT}${searchTerm}`);
    e.preventDefault();
  };

  return (
    <div>
      <TodoApp />

      <h1>My React</h1>
      {/* <form onSubmit={handleSearchSubmit}>
        <InputWithLabel
          onInputChange={handleSearchInput}
          value={searchTerm}
          id="search"
          label="Search"
          isFocused={true}
        >
          <strong>Search for:</strong>
        </InputWithLabel>
        <button type="submit" disabled={!searchTerm}>
          Search
        </button>
      </form> */}
      <Form
        searchTerm={searchTerm}
        onSearchSubmit={handleSearchSubmit}
        handleSearchInput={handleSearchInput}
      />

      <hr />

      {stories.isError && (
        <p>
          <strong>Oops! Something went wrong...</strong>
        </p>
      )}

      {stories.isLoading ? (
        <p>
          <strong>Loading...</strong>
        </p>
      ) : (
        <List list={stories.data} onRemoveItem={handleRemoveStory} />
      )}
    </div>
  );
};

export default App;

const Form = ({ onSearchSubmit, handleSearchInput, searchTerm }) => {
  <div>
    <form onSubmit={onSearchSubmit}>
      <InputWithLabel
        onInputChange={handleSearchInput}
        value={searchTerm}
        id="search"
        label="Search"
        isFocused={true}
      >
        <strong>Search for:</strong>
      </InputWithLabel>
      <button type="submit" disabled={!searchTerm}>
        Search
      </button>
    </form>
  </div>;
};

export const InputWithLabel = ({
  isFocused,
  children,
  type = "text",
  id,
  value,
  onInputChange,
  checked,
}) => {
  const inputRef = useRef(null);
  useEffect(() => {
    if (isFocused && inputRef.current) {
      inputRef.current.focus();
    }
  });

  return (
    <Fragment>
      <label htmlFor={id}>
        {children}
        &nbsp;
        <input
          id={id}
          type={type}
          value={value}
          onChange={onInputChange}
          ref={inputRef}
          checked={checked}
        />
      </label>
    </Fragment>
  );
};

const List = ({ list, onRemoveItem }) => (
  <ul>
    {list.map((item) => (
      <Item key={item.objectID} item={item} onRemoveItem={onRemoveItem} />
    ))}
  </ul>
);

const Item = ({ item, onRemoveItem }) => {
  return (
    <li>
      <a href={item.url}>{item.title}: </a>
      <span>{item.author} </span>
      <p>Comments: {item.num_comments}</p>
      <p>Points: {item.points}</p>
      <span>
        <button type="button" onClick={() => onRemoveItem(item)}>
          Dismiss
        </button>
      </span>
      <br />
      <br />
    </li>
  );
};
