import React, { useEffect, useReducer, useState } from "react";

const todoReducer = (state, action) => {
  switch (action.type) {
    case "SET_TODOS":
      return action.payload;
    case "DO_TODO":
      return state.map((todo) => {
        if (todo.id === action.id) {
          return { ...todo, complete: true };
        } else {
          return todo;
        }
      });
    case "UNDO_TODO":
      return state.map((todo) => {
        if (todo.id === action.id) {
          return { ...todo, complete: false };
        } else {
          return todo;
        }
      });
    case "REMOVE_TODO":
      return state.filter((todo) => todo.id !== action.payload.id);
    default:
      return state;
    case "ADD_TODO":
      return state.concat({
        id: Math.random(),
        task: action.payload,
        complete: false,
      });
  }
};

const TodoApp = () => {
  const initialTodos = [
    {
      id: "a",
      task: "Learn React",
      complete: false,
    },
    {
      id: "b",
      task: "Learn Firebase",
      complete: false,
    },
  ];

  const [todos, dispatchTodos] = useReducer(todoReducer, []);

  const getAsyncTodos = () =>
    new Promise((resolve) =>
      setTimeout(
        () =>
          resolve({
            dataTodos: {
              todos: initialTodos,
            },
          }),
        2000
      )
    );

  const handleTodoStatus = (todo) => {
    dispatchTodos({
      type: todo.complete ? "UNDO_TODO" : "DO_TODO",
      id: todo.id,
    });
  };

  const handleRemoveTodo = (todo) => {
    dispatchTodos({
      type: "REMOVE_TODO",
      payload: todo,
    });
  };

  const [todoQuery, setTodoQuery] = useState("");

  const handleTodoQuery = (e) => {
    setTodoQuery(e.target.value);
  };

  const handleAddTodo = (todoQuery, e) => {
    dispatchTodos({
      type: "ADD_TODO",
      payload: todoQuery,
    });
  };

  const onSubmitTodo = (e) => {
    e.preventDefault();
    handleAddTodo(todoQuery);
  };

  useEffect(() => {
    getAsyncTodos().then((result) => {
      dispatchTodos({
        type: "SET_TODOS",
        payload: result.dataTodos.todos,
      });
    });
  }, []);
  return (
    <div>
      <h1> Todo List </h1>
      <ul>
        {todos.map((todo) => (
          <li key={todo.id}>
            <button onClick={() => handleRemoveTodo(todo)}> Delete </button> &
            nbsp;{" "}
            <InputWithLabel
              checked={todo.comple}
              type="checkbox"
              onInputChange={() => handleTodoStatus(todo)}
              value={todo.task}
            >
              {todo.task}
            </InputWithLabel>
          </li>
        ))}
      </ul>
      <form onSubmit={onSubmitTodo}>
        <InputWithLabel type="text" onInputChange={handleTodoQuery}>
          Add Todo:
        </InputWithLabel>
        <button type="button"> Add </button>
      </form>
    </div>
  );
};

export default TodoApp;
