import React from "react";
import { sortBy } from "lodash";
// import { InputWithLabel } from "./InputWithLabel";

const SORTS = {
  NONE: (list) => list,
  TITLE: (list) => sortBy(list, "title"),
};

export const List = ({
  list,
  onRemoveTodo,
  onCheckedTodo,
  onCheckboxChange,
}) => {
  const [sort, setSort] = React.useState({
    sortKey: "NONE",
    isReversed: false,
  });
  const handleSort = (sortKey) => {
    const isReversed = sort.sortKey === sortKey && !sort.isReversed;
    setSort({ sortKey, isReversed });
  };

  const sortFunction = SORTS[sort.sortKey];
  const sortedList = sort.isReversed
    ? sortFunction(list).reverse()
    : sortFunction(list);

  return (
    <div>
      <div>
        <label htmlFor="markAllTodos">
          <input
            id="markAllTodos"
            type="checkbox"
            onChange={(e) => onCheckboxChange(list, e)}
          />

          <span>Mark all works</span>
        </label>
      </div>

      <div className="d-flex justify-content-start align-items-center my-3 mr-2">
        <button
          className="btn btn-success"
          type="button"
          onClick={() => handleSort("TITLE")}
          style={{ width: "85%" }}
        >
          Title
        </button>
        <button className="btn btn-success" style={{ width: "15%" }}>
          Dismiss
        </button>
      </div>

      <div>
        {sortedList.map((todo) => (
          <Item
            todo={todo}
            onRemoveTodo={onRemoveTodo}
            onCheckedTodo={onCheckedTodo}
            key={todo.id}
          />
        ))}
        80
      </div>
    </div>
  );
};

const Item = ({ todo, onRemoveTodo, onCheckedTodo }) => {
  return (
    <div className="d-flex justify-content-start align-items-center">
      <span style={{ width: "5%" }}>
        <input
          value={todo.title}
          id={todo.id}
          type="checkbox"
          onChange={() => onCheckedTodo(todo)}
          checked={todo.completed}
        />
      </span>

      <span
        style={{
          whiteSpace: "nowrap",
          overflow: "hidden",
          textOverflow: "ellipsis",
          textTransform: "capitalize",
          width: "80%",
        }}
      >
        <label htmlFor={todo.id}>
          <span>{todo.title}</span>
        </label>
      </span>

      <span style={{ width: "15%", textAlign: "center" }}>
        <button className="btn btn-primary" onClick={() => onRemoveTodo(todo)}>
          Delete
        </button>
      </span>

      {/*   <InputWithLabel
        id={todo.id}
        value={todo.title}
        checked={todo.completed}
        type="checkbox"
        onInputChange={() => onCheckedTodo(todo)}
      >
        <span
          style={{
            whiteSpace: "nowrap",
            overFlow: "hidden",
            textOverflow: "ellipsis",
          }}
        >
          {todo.title}
        </span>
      </InputWithLabel> */}
    </div>
  );
};
