import { Fragment, useReducer } from "react";
import { useEffect, useState, useRef } from "react";
import "./App.css";
//Custom HooksetValue
const usePersistentState = (key, initialState) => {
  const [value, setValue] = useState(localStorage.getItem(key) || initialState);

  useEffect(() => {
    localStorage.setItem(key, value);
  }, [key, value]);

  return [value, setValue];
};

const storiesReducer = (state, action) => {
  switch (action.type) {
    case "SET_STORIES":
      return action.payload;
    default:
      throw new Error();
  }
};
const App = () => {
  const initialStories = [
    {
      title: "React",
      url: "https://reactjs.org/",
      author: "Jordan Walke",
      num_comments: 3,
      points: 4,
      objectID: 0,
    },
    {
      title: "Redux",
      url: "https://redux.js.org/",
      author: "Dan Abramov, Andrew Clark",
      num_comments: 2,
      points: 5,
      objectID: 1,
    },
  ];

  const [stories, dispatchStories] = useReducer(storiesReducer, []);

  const getAsyncStories = () =>
    new Promise((resolve) =>
      setTimeout(() => resolve({ data: { stories: initialStories } }), 2000)
    );

  const [searchTerm, setSearchTerm] = usePersistentState("search", "Redux");
  const [isLoading, setIsLoading] = useState(false);
  const [isError, setIsError] = useState(false);

  useEffect(() => {
    setIsLoading(true);
    getAsyncStories()
      .then((result) => {
        dispatchStories({
          type: "SET_STORIES",
          payload: result.data.stories
        });
        setIsLoading(false);
      })
      .catch(() => setIsError(true));
  }, []);

  const handleRemoveStory = (item) => {
    const newStories = stories.filter(
      (story) => story.objectID !== item.objectID
    );
    dispatchStories({
      type: "SET_STORIES",
      payload: newStories,
    });
  };

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  const searchedStories = stories.filter((story) => {
    return story.title.toLowerCase().includes(searchTerm.toLowerCase());
  });

  return (
    <div>
      <h1>My React AponInputChangep</h1>
      <InputWithLabel
        onInputChange={handleSearch}
        value={searchTerm}
        id="search"
        label="Search"
        isFocused={true}
        // or isFocused =  (isFocused = {true})
      >
        <StringChildren text="Search for:" />
      </InputWithLabel>

      <hr />

      {isError && (
        <p>
          <strong>Oops! Something went wrong...</strong>
        </p>
      )}

      {isLoading ? (
        <p>
          <strong>Loading...</strong>
        </p>
      ) : (
        <List list={searchedStories} onRemoveItem={handleRemoveStory} />
      )}
    </div>
  );
};

export default App;

const InputWithLabel = ({
  isFocused,
  children,
  type = "text",
  id,
  value,
  onInputChange,
}) => {
  const inputRef = useRef(null);
  useEffect(() => {
    if (isFocused && inputRef.current) {
      inputRef.current.focus();
    }
  });

  return (
    <Fragment>
      <label htmlFor={id}>{children}</label>
      &nbsp;
      <input
        id={id}
        type={type}
        value={value}
        onChange={onInputChange}
        ref={inputRef}
      />
    </Fragment>
  );
};

const StringChildren = ({ text }) => {
  return <strong>{text}</strong>;
};

const List = ({ list, onRemoveItem }) => (
  <ul>
    {list.map((item) => (
      <Item key={item.objectID} item={item} onRemoveItem={onRemoveItem} />
    ))}
  </ul>
);

const Item = ({ item, onRemoveItem }) => {
  return (
    <li>
      <a href={item.url}>{item.title}: </a>
      <span>{item.author} </span>
      <p>Comments: {item.num_comments}</p>
      <p>Points: {item.points}</p>
      <span>
        <button type="button" onClick={() => onRemoveItem(item)}>
          Dismiss
        </button>
      </span>
      <br />
      <br />
    </li>
  );
};
