import React, { useState, useEffect } from "react";
import {
  Button,
  Modal,
  Form,
  ProgressBar,
  Alert,
  Image,
  Spinner,
  Row,
  Col,
  Container,
  Card,
} from "react-bootstrap";

import { useAuth } from "../../contexts/AuthContext";
import { Prompt } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { storage } from "../../firebase/config";
import {
  ref,
  uploadBytesResumable,
  getDownloadURL,
  deleteObject,
  listAll,
} from "firebase/storage";

export default function Homepage() {
  const { user } = useAuth();
  const [error, setError] = useState("");
  const [show, setShow] = useState(false);
  const [isBlocking, setIsBlocking] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  const [progress, setProgress] = useState(null);
  const [urls, setUrls] = useState([]);
  const [imgUrls, setImgUrls] = useState([]);

  const handleUploadFiles = (path, file) => {
    return new Promise((resolve) => {
      setError("");
      setIsLoading(true);

      if (file) {
        const imageRef = ref(storage, `${user.email}/${path}/${file.name}`);
        const metadata = {
          contentType: "image/jpeg",
        };
        const uploadTask = uploadBytesResumable(imageRef, file, metadata);

        uploadTask.on(
          "state_changed",
          (snapshot) => {
            const progress =
              (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
            setProgress(progress);
          },
          (error) => setError(error.message),
          async () => {
            const url = await getDownloadURL(imageRef);
            setUrls((prevState) => [...prevState, url]);
            setProgress(null);
            setIsLoading(false);
          }
        );
      }
    });
  };

  const promises = [];
  const [photoUrls, setPhotoUrls] = useState([]);

  const handleInputChange = (e) => {
    if (e.target.files.length > 0) {
      for (let i = 0; i < e.target.files.length; i++) {
        const newImage = e.target.files[i];
        promises.push(handleUploadFiles("Images", newImage));
      }
      Promise.all(promises).then(() => {
        setPhotoUrls(urls);
      });
    }
  };

  const handleRemoveFile = async (url) => {
    const httpRef = ref(storage, url);
    try {
      await deleteObject(httpRef);
      const newUrls = urls.filter((fileUrl) => fileUrl !== url);
      setUrls(newUrls);
    } catch (error) {
      setError(error.message);
    }
  };

  //Missing Upload Photo To Firebase Storage with another path
  const handleUpload = async () => {
    try {
      setImgUrls(imgUrls.concat(urls));
      handleClose();
      setIsBlocking(false);
    } catch (error) {
      setError(error.message);
    }
  };

  const loadImages = React.useCallback(() => {
    const listRef = ref(storage, `${user.email}/Images`);
    listAll(listRef)
      .then((res) => {
        res.items.forEach(async (itemRef) => {
          const url = await getDownloadURL(itemRef);
          setImgUrls((prevState) => [...prevState, url]);
        });
      })
      .catch((error) => setError(error.message));
    //eslint-disable-next-line
  }, []);

  useEffect(() => {
    loadImages();
    console.log("Mount");
  }, [loadImages]);

  useEffect(() => {
    return () => {
      if (isBlocking & (photoUrls.length > 0)) {
        photoUrls.forEach((url) => {
          const httpRef = ref(storage, url);
          deleteObject(httpRef)
            .then(() => alert("DELETE OK"))
            .catch((error) => alert("ERROR"));
        });
        console.log("Unmount");
      }
    };
  }, [photoUrls, isBlocking]);

  console.log({ isBlocking, urls, imgUrls, photoUrls });

  const handleClose = () => {
    setShow(false);
    /* setUrls([]);
    setIsBlocking(false);
    urls.forEach((url) => {
      const httpRef = ref(storage, url);
      deleteObject(httpRef);
    }); */
  };

  const handleShow = () => setShow(true);

  const UploadModal = (
    <div>
      {error && <Alert variant="danger">{error}</Alert>}
      <Prompt
        when={isBlocking}
        message={(location) => {
          return location.pathname === "/"
            ? true
            : JSON.stringify({
                header: "Leave this page?",
                content:
                  "You are not finishing your works. Are you sure want to leave?",
              });
        }}
      />

      <Modal show={show} onHide={handleClose}>
        <Modal.Body>
          <Form.Control
            id="photo"
            type="file"
            multiple
            onChange={(e) => {
              setIsBlocking(e.target.files.length > 0);
              handleInputChange(e);
            }}
            accept=".jpg, .jpeg, .png"
          />

          {isLoading ? (
            <div className="d-flex flex-column justify-content-center align-items-center my-5">
              <Spinner animation="border" size="lg" variant="primary" />
            </div>
          ) : (
            <>
              {urls.map((fileUrl) => {
                return (
                  <div>
                    {fileUrl && (
                      <div
                        style={{ position: "relative" }}
                        className="d-flex justify-content-center align-items-center"
                      >
                        <div
                          style={{
                            position: "absolute",
                            top: "1.25rem",
                            right: "0.75rem",
                            cursor: "pointer",
                          }}
                        >
                          <FontAwesomeIcon
                            className="closed-icon"
                            icon={["fas", "times"]}
                            size="lg"
                            onClick={() => handleRemoveFile(fileUrl)}
                          />
                        </div>

                        <div>
                          <Image
                            variant="top"
                            src={fileUrl}
                            alt="Avatar"
                            className="my-3"
                            fluid
                            style={{
                              borderRadius: "10px",
                              width: "50vw",
                            }}
                          />
                        </div>

                        {progress && (
                          <ProgressBar
                            animated
                            now={progress}
                            label={`Loading...${progress}%`}
                            max="100"
                            className="w-75 mb-3"
                          />
                        )}
                      </div>
                    )}
                  </div>
                );
              })}
            </>
          )}
        </Modal.Body>

        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleUpload}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );

  const Pictures = (
    <Card>
      <Card.Header>
        <Card.Title>Pictures</Card.Title>
      </Card.Header>
      <Card.Body>
        <Row>
          {imgUrls.map((url) => (
            <Col xs={4} className="p-4 w-25">
              <Image
                src={url}
                alt={`${user.displayName}-photoUpload`}
                width="100px"
                height="100px"
              />
            </Col>
          ))}
        </Row>
      </Card.Body>
    </Card>
  );

  return (
    <Container className="bg-white">
      {UploadModal}
      <Row>
        <Col md={4}>{Pictures}</Col>

        <Col xs>
          <h1>Homepage</h1>
          <div className="text-center">
            <textarea
              type="text"
              placeholder="What are you thinking?"
              style={{ padding: "1rem", borderRadius: "10px" }}
              column="500px"
            />
          </div>

          <div className="my-3">
            <Button variant="outline-primary" onClick={handleShow}>
              <FontAwesomeIcon icon={["fas", "images"]} size="lg" />
            </Button>
          </div>
        </Col>
      </Row>
    </Container>
  );
}
