import { Fragment, useReducer } from "react";
import { useEffect, useState, useRef } from "react";
import "./App.css";

//Custom HooksetValue
const usePersistentState = (key, initialState) => {
  const [value, setValue] = useState(localStorage.getItem(key) || initialState);

  useEffect(() => {
    localStorage.setItem(key, value);
  }, [key, value]);

  return [value, setValue];
};

//Reducer
//Todo reducer
const todosReducer = (state, action) => {
  switch (action.type) {
    case "DO_TODO":
      return state.map((todo) => {
        if (todo.id === action.id) {
          return { ...todo, complete: true };
        } else {
          return todo;
        }
      });
    default:
      return state;
  }
};

//stories reducer
const storiesReducer = (state, action) => {
  switch (action.type) {
    case "SET_STORIES":
      return action.payload;
    case "REMOVE_STORY":
      return state.filter(
        (story) => story.objectID !== action.payload.objectID
      );
    default:
      throw new Error();
  }
};
const App = () => {
  const initialTodos = [
    {
      id: "a",
      task: "Learn React",
      complete: false,
    },
    {
      id: "b",
      task: "Learn Firebase",
      complete: false,
    },
  ];

  const initialStories = [
    {
      title: "React",
      url: "https://reactjs.org/",
      author: "Jordan Walke",
      num_comments: 3,
      points: 4,
      objectID: 0,
    },
    {
      title: "Redux",
      url: "https://redux.js.org/",
      author: "Dan Abramov, Andrew Clark",
      num_comments: 2,
      points: 5,
      objectID: 1,
    },
  ];

  const [stories, dispatchStories] = useReducer(storiesReducer, []);
  const [todos, dispatchTodos] = useReducer(todosReducer, initialTodos);

  const getAsyncStories = () =>
    new Promise((resolve) =>
      setTimeout(() => resolve({ data: { stories: initialStories } }), 2000)
    );

  const [searchTerm, setSearchTerm] = usePersistentState("search", "Redux");
  const [isLoading, setIsLoading] = useState(false);
  const [isError, setIsError] = useState(false);

  useEffect(() => {
    setIsLoading(true);
    getAsyncStories()
      .then((result) => {
        dispatchStories({
          type: "SET_STORIES",
          payload: result.data.stories,
        });
        setIsLoading(false);
      })
      .catch(() => setIsError(true));
  }, []);

  const handleRemoveStory = (item) => {
    dispatchStories({
      type: "REMOVE_STORY",
      payload: item,
    });
  };

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  const searchedStories = stories.filter((story) => {
    return story.title.toLowerCase().includes(searchTerm.toLowerCase());
  });

  const handleTodoTask = (todo) => {
    dispatchTodos({
      type: "DO_TODO",
      payload: {
        id: todo.id,
      },
    });
  };

  return (
    <div>
      <ul>
        {todos.map((todo) => (
          <li key={todo.id}>
            <label>
              <input
                type="checkbox"
                checked={todo.complete}
                onChange={() => handleTodoTask(todo)}
              />
              {todo.task}
            </label>
          </li>
        ))}
      </ul>

      <h1>My React AponInputChangep</h1>
      <InputWithLabel
        onInputChange={handleSearch}
        value={searchTerm}
        id="search"
        label="Search"
        /* isFocused={true} */
      >
        <strong>Search for:</strong>
      </InputWithLabel>

      <hr />

      {isError && (
        <p>
          <strong>Oops! Something went wrong...</strong>
        </p>
      )}

      {isLoading ? (
        <p>
          <strong>Loading...</strong>
        </p>
      ) : (
        <List list={searchedStories} onRemoveItem={handleRemoveStory} />
      )}
    </div>
  );
};

export default App;

const InputWithLabel = ({
  isFocused,
  children,
  type = "text",
  id,
  value,
  onInputChange,
  checked,
}) => {
  const inputRef = useRef(null);
  useEffect(() => {
    if (isFocused && inputRef.current) {
      inputRef.current.focus();
    }
  });

  return (
    <Fragment>
      <label htmlFor={id}>
        {children}
        &nbsp;
        <input
          id={id}
          type={type}
          value={value}
          onChange={onInputChange}
          ref={inputRef}
          checked={checked}
        />
      </label>
    </Fragment>
  );
};

const List = ({ list, onRemoveItem }) => (
  <ul>
    {list.map((item) => (
      <Item key={item.objectID} item={item} onRemoveItem={onRemoveItem} />
    ))}
  </ul>
);

const Item = ({ item, onRemoveItem }) => {
  return (
    <li>
      <a href={item.url}>{item.title}: </a>
      <span>{item.author} </span>
      <p>Comments: {item.num_comments}</p>
      <p>Points: {item.points}</p>
      <span>
        <button type="button" onClick={() => onRemoveItem(item)}>
          Dismiss
        </button>
      </span>
      <br />
      <br />
    </li>
  );
};
