import React from "react";
import { BrowserRouter as Router, Route, Link, Switch } from "react-router-dom";
import TodoApp from "./TodoApp";
import HackerNewStories from "./HackerNewStoriesApp";
import Home from "./Home";
import Login from "./Login";
import SignUp from "./SignUp";
import PrivateRoute from "./PrivateRoute";
import "./FontAwesome";
import "./App.css";
import ScrollTopArrow from "./ScrollTopArrow";
import { Navbar } from "react-bootstrap";
import { AuthProvider } from "../contexts/AuthContext";
import {
  createBrowserHistory
} from 'history';

export const App = () => {
  return (
    <Router history={history}>
      <Navbar className="d-flex justify-content-end ">
        <ul className="nav">
          <li className="nav-item">
            <Link to="/home" style={{ textDecoration: "none" }}>
              <span className="nav-link">Home</span>
            </Link>
          </li>
          <li className="nav-item">
            <Link to="/login" style={{ textDecoration: "none" }}>
              <span className="nav-link">Login</span>
            </Link>
          </li>
          <li className="nav-item">
            <Link to="/signup" style={{ textDecoration: "none" }}>
              <span className="nav-link">Sign Up</span>
            </Link>
          </li>
          <li className="nav-item">
            <Link to="/todo-app" style={{ textDecoration: "none" }}>
              <span className="nav-link">Todo App</span>
            </Link>
          </li>
          <li className="nav-item">
            <Link to="/stories-app" style={{ textDecoration: "none" }}>
              <span className="nav-link">Hacker News Stories App</span>
            </Link>
          </li>
        </ul>
      </Navbar>

      <AuthProvider>
        <Switch>
          <PrivateRoute path="/todo-app">
            <TodoApp />
          </PrivateRoute>
          <PrivateRoute path="/stories-app">
            <HackerNewStories />
          </PrivateRoute>
          <Route path="/login">
            <Login />
          </Route>
          <Route path="/signup">
            <SignUp />
          </Route>
          <PrivateRoute exact path="/home">
            <Home />
          </PrivateRoute>
        </Switch>
      </AuthProvider>

      <ScrollTopArrow />
    </Router>
  );
};

export default App;
export const history = createBrowserHistory();
