import React, { useState, useEffect } from "react";
import { Button, Alert, Card } from "react-bootstrap";
import { auth } from "../../firebase/config";
import { useAuth } from "../../contexts/AuthContext";
import { useHistory, Link } from "react-router-dom";
import {
  collection,
  onSnapshot,
  doc,
  getDoc,
  query,
  where,
  getDocs,
} from "firebase/firestore";
import { db } from "../../firebase/config";
// import { useFilestore } from "../hooks/useFilestore";

export const Home = () => {
  const { logout, user } = useAuth();
  const [error, setError] = useState("");
  const history = useHistory();

  const handleLogOut = async () => {
    setError("");

    try {
      await logout(auth);
      history.push("/login");
    } catch (error) {
      setError(error.message);
    }
  };

  useEffect(() => {
    //Listen to multiple documents in a collection
    const q2 = query(collection(db, "users"));

    const unsubcribe = onSnapshot(q2, (snapshot) => {
      const data = snapshot.docs.map((doc) => ({
        ...doc.data(),
        id: doc.id,
      }));
      console.log("Current Data: ", data);
    });

    const getDocument = async () => {
      //Get a document
      const docRef = doc(db, "cities", "SF");
      const docSnap = await getDoc(docRef);

      if (docSnap.exists()) {
        console.log("Get a Document data:", docSnap.data());
      } else {
        // doc.data() will be undefined in this case
        console.log("No such document!");
      }

      //Get all documents ( omitted where)
      const q1 = query(collection(db, "cities"), where("capital", "==", true));
      const querySnapshot = await getDocs(q1);
      querySnapshot.forEach((doc) => {
        console.log("Get all document data", doc.id, "=>", doc.data());
      });
    };

    getDocument();

    //Listen to a document
    const q3 = query(doc(db, "cities", "BJ"));
    const unsub3 = onSnapshot(q3, (doc) =>
      console.log("Data of document BJ", {
        ...doc.data(),
        id: doc.id,
      })
    );

    //Listen to all documents
    const q4 = query(collection(db, "cities"), where("state", "==", "CA"));
    const unsub4 = onSnapshot(q4, (querySnapshot) => {
      const cities = [];
      querySnapshot.forEach((doc) => {
        cities.push(doc.data().name);
      });
      console.log("Current cities in CA: ", cities.join(", "));
    });

    return { unsubcribe, unsub3, unsub4 };
  }, []);

  return (
    <Card className="d-flex flex-row justify-content-center align-items-center">
      <Card.Body style={{ maxWidth: "400px" }}>
        <Card.Header as="h1" className="mb-4">
          Profile
        </Card.Header>

        {error && <Alert variant="danger">{error}</Alert>}

        <div className="text-center mb-3">
          <Card.Img
            variant="top"
            src={user.photoURL}
            alt="Avatar"
            style={{
              width: "100px",
              height: "100px",
              borderRadius: "50%",
            }}
          ></Card.Img>
        </div>

        <Card.Text className="text-center">
          <strong>Email:</strong> {user.email}
        </Card.Text>

        <Card.Text className="text-center">
          <strong>Name:</strong> {user.displayName}
        </Card.Text>

        <div className="text-center mb-3">
          <Button>
            <Link to="/update-profile" className="text-white list-style-none">
              Update Profile
            </Link>
          </Button>
        </div>

        <div className="text-center w-100 mb-3">
          <Button onClick={handleLogOut}>Log Out</Button>
        </div>
      </Card.Body>
    </Card>
  );
};

export default Home;
