import React, { useEffect, useReducer, useState } from "react";
import axios from "axios";

const todoReducer = (state, action) => {
  switch (action.type) {
    case "TODO_FETCH_INIT":
      return {
        ...state,
        isLoading: true,
        isError: false,
      };
    case "TODO_FETCH_SUCCESS":
      return {
        ...state,
        isLoading: false,
        isError: false,
        data: action.payload,
      };
    case "TODO_FETCH_FAILURE":
      return {
        ...state,
        isLoading: false,
        isError: true,
      };
    case "DO_TODO":
      return {
        ...state,
        data: state.data.map((todo) => {
          if (todo.id === action.id) {
            return { ...todo, completed: true };
          } else {
            return todo;
          }
        }),
      };
    case "UNDO_TODO":
      return {
        ...state,
        data: state.data.map((todo) => {
          if (todo.id === action.id) {
            return { ...todo, completed: false };
          } else {
            return todo;
          }
        }),
      };
    case "REMOVE_TODO":
      return {
        ...state,
        data: state.data.filter((todo) => todo.id !== action.payload.id),
      };

    case "ADD_TODO":
      return {
        ...state,
        data: state.data.concat({
          id: Math.random(),
          title: action.payload,
          completed: false,
        }),
      };
    default:
      return state;
  }
};

const TodoApp = () => {
  const [todos, dispatchTodos] = useReducer(todoReducer, {
    data: [],
    isLoading: false,
    isError: false,
  });

  const [todoQuery, setTodoQuery] = useState("");

  const handleFetchTodos = React.useCallback(async () => {
    dispatchTodos({
      type: "TODO_FETCH_INIT",
    });

    try {
      const respone = await axios.get(
        `https://jsonplaceholder.typicode.com/todos`
      );
      dispatchTodos({
        type: "TODO_FETCH_SUCCESS",
        payload: respone.data,
      });
    } catch {
      dispatchTodos({
        type: "TODO_FETCH_FAILURE",
      });
    }
  }, []);

  useEffect(() => handleFetchTodos(), [handleFetchTodos]);

  const handleCheckedTodo = (todo) => {
    dispatchTodos({
      type: todo.completed ? "UNDO_TODO" : "DO_TODO",
      id: todo.id,
    });
  };

  const handleRemoveTodo = (todo) => {
    dispatchTodos({
      type: "REMOVE_TODO",
      payload: todo,
    });
  };

  const handleAddTodo = (todoQuery) => {
    dispatchTodos({
      type: "ADD_TODO",
      payload: todoQuery,
    });
  };

  const handleAddTodoInput = (e) => {
    setTodoQuery(e.target.value);
  };

  const handleAddTodoSubmit = (e) => {
    e.preventDefault();
    handleAddTodo(todoQuery);
  };

  return (
    <div>
      <h1> Todo List </h1>
      {todos.isError && <p>Oops! Something went wrong</p>}

      {todos.isLoading ? (
        <p>Loading...</p>
      ) : (
        <List
          list={todos.data}
          onRemoveTodo={handleRemoveTodo}
          onCheckedTodo={handleCheckedTodo}
        />
      )}
      <AddTodoForm
        onAddTodoInput={handleAddTodoInput}
        onAddTodoSubmit={handleAddTodoSubmit}
      >
        Add your work:
      </AddTodoForm>
    </div>
  );
};
export default TodoApp;

const InputWithLabel = ({
  isFocused,
  checked,
  value,
  type = "text",
  id,
  children,
  onInputChange,
}) => {
  const inputRef = React.useRef(null);
  if (isFocused && inputRef.current) {
    inputRef.current.focus();
  }

  return (
    <>
      <label htmlFor={id}>{children}</label>
      &nbsp;
      <input
        value={value}
        id={id}
        type={type}
        onChange={onInputChange}
        checked={checked}
        ref={inputRef}
      />
    </>
  );
};

const AddTodoForm = ({ onAddTodoSubmit, onAddTodoInput }) => {
  return (
    <form onSubmit={onAddTodoSubmit}>
      <InputWithLabel onInputChange={onAddTodoInput} id="add">
        Add your work:
      </InputWithLabel>
      <button type="submit"> Add </button>
    </form>
  );
};

const List = ({ list, onRemoveTodo, onCheckedTodo }) => (
  <ul>
    {list.map((todo) => (
      <Item
        todo={todo}
        onRemoveTodo={onRemoveTodo}
        onCheckedInput={onCheckedTodo}
        key={todo.id}
      />
    ))}
  </ul>
);

const Item = ({ todo, onRemoveTodo, onCheckedInput }) => (
  <li>
    <InputWithLabel
      id={todo.id}
      value={todo.title}
      checked={todo.completed}
      type="checkbox"
      onInputChange={() => onCheckedInput(todo)}
    >
      {todo.title}
    </InputWithLabel>
    <button onClick={() => onRemoveTodo(todo)}> Delete </button>
  </li>
);
