import React, { useState } from "react";
import {} from "react-bootstrap";
import { storage } from "../../firebase/config";
import { ref, uploadBytesResumable, getDownloadURL } from "firebase/storage";

export default function Homepage() {
  const [images, setImages] = useState([]);
  const [urls, setUrls] = useState([]);
  const [progress, setProgress] = useState(0);
  const [error, setError] = useState();

  const handleInputChange = (e) => {
    for (let i = 0; i < e.target.files.length; i++) {
      setImages((prevState) => [...prevState, e.target.files[i]]);
    }
  };

  console.log(images);

  const handleUploadImages = () => {
    const promises = [];
    images.forEach((image) => {
      const imagesRef = ref(storage, "images/" + image.name);
      const metadata = {
        contentType: "image/jpeg",
      };
      const uploadTask = uploadBytesResumable(imagesRef, image, metadata);
      promises.push(uploadTask);
      setProgress(true);
      uploadTask.on(
        "state_changed",
        (snapshot) => {
          const progress =
            (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
          setProgress(progress);
        },
        (error) => setError(error.message),
        async () => {
          const url = await getDownloadURL(imagesRef);
          setUrls((prevState) => [...prevState, url]);
        }
      );
    });

    Promise.all(promises)
      .then(
        () => setProgress(false),
        alert("Upload Multiple Files Successfull")
      )
      .catch((error) => setError(error.message));
  };

  console.log({ urls });
  return (
    <div>
      <h1>Homepage</h1>
      <input multiple type="file" onChange={(e) => handleInputChange(e)} />
      <button onClick={handleUploadImages}>Upload</button>
    </div>
  );
}
