import { Fragment, useReducer } from "react";
import { useEffect, useState, useRef } from "react";
import "./App.css";
import TodoApp from "./TodoApp";

//Custom Hook
const usePersistentState = (key, initialState) => {
  const [value, setValue] = useState(localStorage.getItem(key) || initialState);

  useEffect(() => {
    localStorage.setItem(key, value);
  }, [key, value]);

  return [value, setValue];
};

//Reducer
//Todos reducer
/* const todoReducer = (state, action) => {
  switch (action.type) {
    case "SET_TODOS":
      return action.payload;
    case "DO_TODO":
      return state.map((todo) => {
        if (todo.id === action.id) {
          return { ...todo, complete: true };
        } else {
          return todo;
        }
      });
    case "UNDO_TODO":
      return state.map((todo) => {
        if (todo.id === action.id) {
          return { ...todo, complete: false };
        } else {
          return todo;
        }
      });
    case "REMOVE_TODO":
      return state.filter((todo) => todo.id !== action.payload.id);
    default:
      return state;
    case "ADD_TODO":
      return state.concat({
        id: Math.random(),
        task: action.payload,
        complete: false,
      });
  }
}; */

//Stories reducer
const storiesReducer = (state, action) => {
  switch (action.type) {
    case "STORIES_FETCH_INIT":
      return {
        ...state,
        isLoading: true,
        isError: false,
      };
    case "STORIES_FETCH_SUCCESS":
      return {
        ...state,
        isLoading: false,
        isError: false,
        data: action.payload,
      };
    case "STORIES_FETCH_FAILURE":
      return {
        ...state,
        isLoading: false,
        isError: true,
      };
    case "REMOVE_STORY":
      return {
        ...state,
        data: state.data.filter(
          (story) => story.objectID !== action.payload.objectID
        ),
      };
    default:
      throw new Error();
  }
};

const App = () => {
  /* const initialTodos = [
    {
      id: "a",
      task: "Learn React",
      complete: false,
    },
    {
      id: "b",
      task: "Learn Firebase",
      complete: false,
    },
  ];

  const [todos, dispatchTodos] = useReducer(todoReducer, []);

  const getAsyncTodos = () =>
    new Promise((resolve) =>
      setTimeout(() => resolve({ dataTodos: { todos: initialTodos } }), 2000)
    );

  const handleTodoStatus = (todo) => {
    dispatchTodos({
      type: todo.complete ? "UNDO_TODO" : "DO_TODO",
      id: todo.id,
    });
  };

  const handleRemoveTodo = (todo) => {
    dispatchTodos({
      type: "REMOVE_TODO",
      payload: todo,
    });
  };

  const [todoQuery, setTodoQuery] = useState("");

  const handleTodoQuery = (e) => {
    setTodoQuery(e.target.value);
  };

  const handleAddTodo = (todoQuery, e) => {
    dispatchTodos({
      type: "ADD_TODO",
      payload: todoQuery,
    });
  };

  const onSubmitTodo = (e) => {
    e.preventDefault();
    handleAddTodo(todoQuery);
  }; */

  const initialStories = [
    {
      title: "React",
      url: "https://reactjs.org/",
      author: "Jordan Walke",
      num_comments: 3,
      points: 4,
      objectID: 0,
    },
    {
      title: "Redux",
      url: "https://redux.js.org/",
      author: "Dan Abramov, Andrew Clark",
      num_comments: 2,
      points: 5,
      objectID: 1,
    },
  ];

  const [stories, dispatchStories] = useReducer(storiesReducer, {
    data: [],
    isLoading: false,
    isError: false,
  });

  const getAsyncStories = () =>
    new Promise((resolve, reject) =>
      setTimeout(() => resolve({ data: { stories: initialStories } }), 2000)
    );

  const [searchTerm, setSearchTerm] = usePersistentState("search", "Redux");

  useEffect(() => {
    /* getAsyncTodos().then((result) => {
      dispatchTodos({
        type: "SET_TODOS",
        payload: result.dataTodos.todos,
      });
    }); */

    dispatchStories({
      type: "STORIES_FETCH_INIT",
    });
    getAsyncStories()
      .then((result) => {
        dispatchStories({
          type: "STORIES_FETCH_SUCCESS",
          payload: result.data.stories,
        });
      })
      .catch(() => dispatchStories({ type: "STORIES_FETCH_FAILURE" }));
  }, []);

  const handleRemoveStory = (item) => {
    dispatchStories({
      type: "REMOVE_STORY",
      payload: item,
    });
  };

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  const searchedStories = stories.data.filter((story) =>
    story.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div>
      {/* <h1>Todo List</h1>
      <ul>
        {todos.map((todo) => (
          <li key={todo.id}>
            <button onClick={() => handleRemoveTodo(todo)}>Delete</button>
            &nbsp;
            <InputWithLabel
              checked={todo.comple}
              type="checkbox"
              onInputChange={() => handleTodoStatus(todo)}
              value={todo.task}
            >
              {todo.task}
            </InputWithLabel>
          </li>
        ))}
      </ul>
      <form onSubmit={onSubmitTodo}>
        <InputWithLabel type="text" onInputChange={handleTodoQuery}>
          Add Todo:
        </InputWithLabel>
        <button type="button">Add</button>
      </form> */}
      <TodoApp />

      <h1>My React</h1>
      <InputWithLabel
        onInputChange={handleSearch}
        value={searchTerm}
        id="search"
        label="Search"
         isFocused={true}
      >
        <strong>Search for:</strong>
      </InputWithLabel>

      <hr />

      {stories.isError && (
        <p>
          <strong>Oops! Something went wrong...</strong>
        </p>
      )}

      {stories.isLoading ? (
        <p>
          <strong>Loading...</strong>
        </p>
      ) : (
        <List list={searchedStories} onRemoveItem={handleRemoveStory} />
      )}
    </div>
  );
};

export default App;

export const InputWithLabel = ({
  isFocused,
  children,
  type = "text",
  id,
  value,
  onInputChange,
  checked,
}) => {
  const inputRef = useRef(null);
  useEffect(() => {
    if (isFocused && inputRef.current) {
      inputRef.current.focus();
    }
  });

  return (
    <Fragment>
      <label htmlFor={id}>
        {children}
        &nbsp;
        <input
          id={id}
          type={type}
          value={value}
          onChange={onInputChange}
          ref={inputRef}
          checked={checked}
        />
      </label>
    </Fragment>
  );
};

const List = ({ list, onRemoveItem }) => (
  <ul>
    {list.map((item) => (
      <Item key={item.objectID} item={item} onRemoveItem={onRemoveItem} />
    ))}
  </ul>
);

const Item = ({ item, onRemoveItem }) => {
  return (
    <li>
      <a href={item.url}>{item.title}: </a>
      <span>{item.author} </span>
      <p>Comments: {item.num_comments}</p>
      <p>Points: {item.points}</p>
      <span>
        <button type="button" onClick={() => onRemoveItem(item)}>
          Dismiss
        </button>
      </span>
      <br />
      <br />
    </li>
  );
};
