import React, { useState } from "react";
import {
  ProgressBar,
  Alert,
  Form,
  Button,
  Row,
  Col,
  Container,
  Image,
  Modal,
  Card,
} from "react-bootstrap";
import { storage } from "../../firebase/config";
import { ref, uploadBytesResumable, getDownloadURL } from "firebase/storage";
import { useAuth } from "../../contexts/AuthContext";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { v1 as uuidv1 } from "uuid";

export default function Homepage() {
  const [images, setImages] = useState([]);
  const [urls, setUrls] = useState([]);
  const [progress, setProgress] = useState();
  const [error, setError] = useState();
  const { user } = useAuth();
  const [show, setShow] = useState(false);

  const handleOpen = () => setShow(true);
  const handleClose = () => setShow(false);

  const handleInputChange = (e) => {
    for (let i = 0; i < e.target.files.length; i++) {
      const newImages = e.target.files[i];
      newImages["id"] = uuidv1();
      setImages((prevState) => [...prevState, newImages]);
    }
  };

  console.log(images) 
  
  const handleUploadImages = () => {
    const promises = [];
    images.forEach((image) => {
      const imagesRef = ref(storage, user.email + "/images/" + image.name);
      const metadata = {
        contentType: "image/jpeg",
      };
      setProgress(true);
      const uploadTask = uploadBytesResumable(imagesRef, image, metadata);
      promises.push(uploadTask);

      uploadTask.on(
        "state_changed",
        (snapshot) => {
          const progress =
            (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
          setProgress(progress);
        },
        (error) => setError(error.message),
        async () => {
          const url = await getDownloadURL(imagesRef);
          setUrls((prevState) => [...prevState, url]);
        }
      );
    });

    Promise.all(promises)
      .then(() => {
        setProgress(false);
      })
      .catch((error) => setError(error.message))
      .finally(() => {
        setUrls([]);
        setImages([]);
      });
  };

  return (
    <Container>
      {error && <Alert variant="danger">{error}</Alert>}

      <h1>Homepage</h1>

      <Card>
        <Card.Body>Alo Alo</Card.Body>
        <Card.Footer>
          <div>
            <span>Add Images</span>
            <FontAwesomeIcon icon={["fas", "images"]} onClick={handleOpen} />
          </div>
        </Card.Footer>
      </Card>

      <Modal show={show} onHide={handleClose}>
        <Modal.Body>
          <input type="file" multiple onChange={(e) => handleInputChange(e)} />

          {progress && (
            <ProgressBar
              now={progress}
              max="100"
              label={`Loading...${progress}%`}
            />
          )}

          <Row>
            {urls.map((url, index) => (
              <Col xs={6} key={url + index} style={{}}>
                <Image fluid src={url} alt={url} style={{ height: "50vw" }} />
              </Col>
            ))}
          </Row>
        </Modal.Body>

        <Modal.Footer>
          <Button onClick={handleClose}>Cancel</Button>
          <Button onClick={handleUploadImages("Images")}>Upload</Button>
        </Modal.Footer>
      </Modal>
    </Container>
  );
}
