import React, { useRef, useState, useEffect } from "react";
import { Form, Card, Button, Alert } from "react-bootstrap";
import { Link, useHistory, useLocation } from "react-router-dom";
import { useAuth } from "../../contexts/AuthContext";
import { addDocument } from "../../firebase/services";
import { useAppContext } from "../../contexts/AppContext";

export const Login = () => {
  const emailRef = useRef();
  const passwordRef = useRef();
  const { user, login, loginWithFacebook, loginWithGoogle } = useAuth();
  const [error, setError] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const history = useHistory();
  const location = useLocation();
  const { from } = location.state || { from: { pathname: "/" } };
  const [isSucced, setIsSucced] = useState(false);
  const { users } = useAppContext();

  useEffect(() => {
    if (user) {
      // users.map((dbUser) => {
      //   if (user.email === dbUser.email) {
      //     return null;
      //   } else {
      //     addDocument("users", {
      //       displayName: user.displayName,
      //       email: user.email,
      //       photoURL: user.photoURL,
      //       uid: user.email,
      //       providerID: user.providerData[0].providerId,
      //     });
      //   }
      //   return null;

      // });

      /*
      users = [{email}, {email}, {email}] 
       */
      function isDifferent(dbUser, index, array) {
        return dbUser.email !== user.email;
      }
      if (users.every(isDifferent)) {
        console.log("hoho");
      }

      // false
      // [(12, 54, 18, 130, 44)].every(isBigEnough); // true
    }
    //eslint-disable-next-line
  }, [user]);
  
  function isBigEnough(element, index, array) {
    return element >= 10;
  }
  console.log([12, 5, 8, 130, 44].every(isBigEnough));
  const handleLoginByPassword = async (e) => {
    e.preventDefault();
    try {
      setError("");
      setIsLoading(true);
      await login(emailRef.current.value, passwordRef.current.value);
      setIsSucced(true);
      setTimeout(() => {
        if (from.pathname === "/login") {
          return history.push("/");
        }
        history.push(from);
      }, 2000);
    } catch (error) {
      const errMessage = error.message.replace("Firebase: ", "");
      setError(errMessage);
    }
    setIsLoading(false);
  };

  const handleLoginByFacebook = async () => {
    try {
      setError("");
      setIsLoading(true);
      await loginWithFacebook();
      setIsSucced(true);
      setTimeout(() => {
        if (from.pathname === "/login") {
          return history.push("/");
        }
        history.push(from);
      }, 2000);
    } catch (error) {
      const errMessage = error.message.replace("Firebase: ", "");
      setError(errMessage);
    }
    setIsLoading(true);
  };

  const handleLoginByGoogle = async () => {
    try {
      setError("");
      await loginWithGoogle();
      setIsSucced(true);
      setTimeout(() => {
        if (from.pathname === "/login") {
          return history.push("/");
        }
        history.push(from);
      }, 2000);
    } catch (error) {
      const errMessage = error.message.replace("Firebase: ", "");
      setError(errMessage);
    }
    setIsLoading(true);
  };

  return (
    <Card className="d-flex flex-row justify-content-center align-items-center">
      <Card.Body style={{ maxWidth: "400px" }}>
        <Card.Header as="h1" className="mb-4">
          My App
        </Card.Header>

        {isSucced && (
          <Alert
            variant="success"
            className="text-center"
            style={{ fontWeight: "500", fontSize: "1.5rem" }}
          >
            Login Successfull. Redirecting....
          </Alert>
        )}

        {error && (
          <Alert
            variant="danger"
            className="text-center"
            style={{ fontWeight: "500", fontSize: "1.5rem" }}
          >
            {error}
          </Alert>
        )}

        <Form onSubmit={handleLoginByPassword}>
          <Form.Group>
            <Form.Label htmlFor="email">Email:</Form.Label>
            <Form.Control
              id="email"
              type="email"
              ref={emailRef}
              required
            ></Form.Control>
          </Form.Group>

          <Form.Group>
            <Form.Label htmlFor="password">Password:</Form.Label>
            <Form.Control
              id="password"
              type="password"
              ref={passwordRef}
              required
            ></Form.Control>
          </Form.Group>

          <div className="text-center my-3">
            <Button disabled={isLoading} type="submit">
              Log in
            </Button>
          </div>
        </Form>

        <Button
          type="button"
          className="w-100 mb-1"
          disabled={isLoading}
          onClick={handleLoginByFacebook}
        >
          Login with Facebook
        </Button>

        <Button
          type="button"
          className="w-100 mb-3"
          disabled={isLoading}
          onClick={handleLoginByGoogle}
        >
          Login with Google
        </Button>

        <div className="text-center mb-2">
          <Link to="/forgot-password">Forgot your password?</Link>
        </div>

        <div className="text-center">
          Need an account? <Link to="/signup">Sign Up</Link>
        </div>
      </Card.Body>
    </Card>
  );
};

export default Login;
