import { db } from "./config";
import { collection, addDoc, serverTimestamp } from "firebase/firestore";

export const addDocuments = async (collections, data) => {
  await addDoc(collection(db, collections), {
    ...data,
    createdAt: serverTimestamp(),
  });
};
