import React from "react";
import firebase, { auth } from "../../../.firebase/config";

export const Login = () => {
  const fbProvider = new firebase.auth.FacebookAuthProvider();

  const handleLogin = () => {
    auth.signInWithPopUp(fbProvider);
  };
  return (
    <div className="text-center">
      <h1>Login to My App</h1>
      <div className="">
        <button className="btn btn-primary w-25" onClick={handleLogin}>Login with Facebook</button>
      </div>

      <div className="">
        <button className="btn btn-primary w-25">Login with Google</button>
      </div>
    </div>
  );
};

export default Login;
