import React, { useState } from "react";
import { Button, Alert, Card, Navbar } from "react-bootstrap";
import { auth } from "../../firebase/config";
import { useAuth } from "../../contexts/AuthContext";
import { useHistory, Link  } from "react-router-dom";

export const Home = () => {
  const { logout, currentUser } = useAuth();
  const [error, setError] = useState("");
  const history = useHistory();

  const handleLogOut = async () => {
    setError("");

    try {
      await logout(auth);
      history.push("/login");
    } catch (error) {
      setError(error.message);
    }
  };
  return (
    <Card>
      <Card.Body>
        <Navbar className="d-flex justify-content-end ">
          <ul className="nav">
            <li className="nav-item">
              <Link to="/" style={{ textDecoration: "none" }}>
                <span className="nav-link">Home</span>
              </Link>
            </li>
            <li className="nav-item">
              <Link to="/todo-app" style={{ textDecoration: "none" }}>
                <span className="nav-link">Todo App</span>
              </Link>
            </li>
            <li className="nav-item">
              <Link to="/stories-app" style={{ textDecoration: "none" }}>
                <span className="nav-link">Hacker News Stories App</span>
              </Link>
            </li>
          </ul>
        </Navbar>

        <h1 className="mb-4">Profile</h1>
        <p className="text-center">
          <strong>Email:</strong> {currentUser.email}
        </p>

        {error && <Alert variant="danger">{error}</Alert>}
      </Card.Body>

      <div className="text-center w-100 mb-4">
        <Button onClick={handleLogOut}>Log Out</Button>
      </div>
    </Card>
  );
};

export default Home;
