import React, { useState } from "react";
import { Navbar, Button } from "react-bootstrap";
import { auth } from "../../firebase/config";
import { useAuth } from "../../contexts/AuthContext";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import TodoApp from "../TodoApp/index";
import HackerNewStories from "../HackerNewStoriesApp/index";
import ScrollTopArrow from "../ScrollTopArrow";

export const Home = () => {
  const { logout } = useAuth();
  const [error, setError] = useState("");

  const handleLogOut = async () => {
    try {
      await logout(auth);
    } catch (error) {
      setError(error);
    }
  };
  return (
    <Router>
      <Navbar className="d-flex justify-content-end ">
        <ul className="nav">
          <li className="nav-item">
            <Link to="/" style={{ textDecoration: "none" }}>
              <span className="nav-link">Home</span>
            </Link>
          </li>
          <li className="nav-item">
            <Link to="/todo-app" style={{ textDecoration: "none" }}>
              <span className="nav-link">Todo App</span>
            </Link>
          </li>
          <li className="nav-item">
            <Link to="/stories-app" style={{ textDecoration: "none" }}>
              <span className="nav-link">Hacker News Stories App</span>
            </Link>
          </li>
          <li className="nav-item">
            <Button onClick={handleLogOut}>Log Out</Button>
          </li>
        </ul>
      </Navbar>

      <Switch>
        <Route path="/todo-app">
          <TodoApp />
        </Route>
        <Route path="/stories-app">
          <HackerNewStories />
        </Route>
        <Route exact path="/">
          <Home />
        </Route>
      </Switch>

      <ScrollTopArrow />
    </Router>
  );
};

export default Home;
