import { Fragment } from "react";
import { useEffect, useState, useRef } from "react";
import "./App.css";
//Custom HooksetValue
const usePersistentState = (key, initialState) => {
  const [value, setValue] = useState(localStorage.getItem(key) || initialState);

  useEffect(() => {
    localStorage.setItem(key, value);
  }, [key, value]);

  return [value, setValue];
};

const App = () => {
  const initialStories = [
    {
      title: "React",
      url: "https://reactjs.org/",
      author: "Jordan Walke",
      num_comments: 3,
      points: 4,
      objectID: 0,
    },
    {
      title: "Redux",
      url: "https://redux.js.org/",
      author: "Dan Abramov, Andrew Clark",
      num_comments: 2,
      points: 5,
      objectID: 1,
    },
  ];
  const [stories, setStories] = useState(initialStories);
  const [searchTerm, setSearchTerm] = usePersistentState("search", "Redux");

  const handleChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleRemoveStory = (item) => {
    const newStories = stories.filter((story) => {
      return item.objectID !== story.objectID;
    });
    setStories(newStories);
  };

  const searchedStories = stories.filter((story) => {
    return story.title.toLowerCase().includes(searchTerm.toLowerCase());
  });

  return (
    <div>
      <h1>My React AponInputChangep</h1>
      <InputWithLabel
        onInputChange={handleChange}
        value={searchTerm}
        id="search"
        label="Search"
        isFocused={true} // or isFocused =  (isFocused = {true})
      >
        <StringChildren text="Search for:" />
      </InputWithLabel>

      <List list={searchedStories} onRemoveItem={handleRemoveStory} />
    </div>
  );
};

export default App;

const InputWithLabel = ({
  isFocused,
  children,
  type = "text",
  id,
  value,
  onInputChange,
}) => {
  const inputRef = useRef(null); //null: initialValue of "current" props

  useEffect(() => {
    if (isFocused && inputRef.current) {
      //  if (isFocused && inputRef.current)
      inputRef.current.focus();
    }
  }, [isFocused]);

  return (
    <>
      <label htmlFor={id}>{children}</label>
      &nbsp;
      <input
        ref={inputRef}
        id={id}
        type={type}
        value={value}
        onChange={onInputChange}
      />
    </>
  );
};

const StringChildren = ({ text }) => {
  return <strong>{text}</strong>;
};

const List = ({ list, onRemoveItem }) => (
  <ul>
    {list.map(({ objectID, ...item }) => (
      <Item key={objectID} item={item} onRemoveItem={onRemoveItem} />
    ))}
  </ul>
);

const Item = ({ item, onRemoveItem }) => {
  function handleRemoveItem() {
    onRemoveItem(item);
  }
  return (
    <li>
      <a href={item.url}>{item.title}: </a>
      <span>{item.author} </span>
      <p>Comments: {item.num_comments}</p>
      <p>Points: {item.points}</p>
      <button type="button" onClick={handleRemoveItem}>Dismiss</button>
      <hr />
    </li>
  );
};
