import React, { useRef, useState } from "react";
/* import firebase, { auth } from "../../firebase/config"; */
import { Form, Card, Button, Alert } from "react-bootstrap";
import { Link } from "react-router-dom";
import { auth } from "../../firebase/config";
import {
  createUserWithEmailAndPassword,
  signOut,
  onAuthStateChanged,
  signInWithEmailAndPassword,
  updateProfile,
} from "firebase/auth";

export const SignUp = () => {
  // const fbProvider = new firebase.auth.FacebookAuthProvider();
  const emailRef = useRef();
  const passwordRef = useRef();
  // const passwordConfirmRef = useRef();

  const [resgisterEmail, setRegisterEmail] = useState("");
  const [resgisterPassWord, setRegisterPassWord] = useState("");
  // const [resgisterConfirmPassWord, setRegisterConfirmPassWord] = useState("");
  const [loginEmail, setLoginEmail] = useState("");
  const [loginPassword, setLoginPassword] = useState("");
  const [user, setUser] = useState({});
  const [isError, setIsError] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");

  const fbuser = auth.currentUser;

  if (fbuser !== null) {
    // The user object has basic properties such as display name, email, etc.
    fbuser.providerData.forEach((profile) => {
      console.log("Sign-in provider: " + profile.providerId);
      console.log("  Provider-specific UID: " + profile.uid);
      console.log("  Name: " + profile.displayName);
      console.log("  Email: " + profile.email);
      console.log("  Photo URL: " + profile.photoURL);
    });
    updateProfile(auth, fbuser, {
      displayName: "Le Khanh",
      photoUrl:
        "https://scontent.fhan2-2.fna.fbcdn.net/v/t1.6435-9/71848503_2563261140434640_1481982318070464512_n.jpg?_nc_cat=106&ccb=1-5&_nc_sid=09cbfe&_nc_ohc=J7gLDWEeRH0AX-eA8AH&_nc_ht=scontent.fhan2-2.fna&oh=cb537a9f13822db2b2afd0c40118b80e&oe=61A64134",
    })
      .then((res) => console.log(res))
      .catch((error) => console.log("Update Error"));
  } else console.log("error");

  onAuthStateChanged(auth, (currentUser) => {
    setUser(currentUser);
  });

  const handleSignUp = async (e) => {
    e.preventDefault();
    try {
      const userCredential = await createUserWithEmailAndPassword(
        auth,
        resgisterEmail,
        resgisterPassWord
      );
      const user = userCredential.user;
      console.log(user);
      setIsError(false);
    } catch (error) {
      setIsError(true);
      const errMes = error.message.replace("Firebase: ", "");
      setErrorMessage(errMes);
    }
  };

  const handleLogOut = async () => {
    await signOut(auth);
    setIsError(false);
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      await signInWithEmailAndPassword(auth, loginEmail, loginPassword);
      setIsError(false);
    } catch (error) {
      setIsError(true);
      const errMes = error.message.replace("Firebase: ", "");
      setErrorMessage(errMes);
    }
  };

  return (
    <Card className="d-flex justify-content-center align-items-center">
      <Card.Body style={{ maxWidth: "400px" }}>
        {isError && (
          <Alert varient="warning" className="text-capitalize">
            {errorMessage}
          </Alert>
        )}
        <h1>My App</h1>
        <Form onSubmit={handleSignUp}>
          <Form.Group>
            <Form.Label>Your Email:</Form.Label>
            <Form.Control
              type="email"
              ref={emailRef}
              required
              onChange={(e) => setRegisterEmail(e.target.value)}
            ></Form.Control>
          </Form.Group>
          <Form.Group>
            <Form.Label>Your Password:</Form.Label>
            <Form.Control
              type="password"
              ref={passwordRef}
              required
              onChange={(e) => setRegisterPassWord(e.target.value)}
            ></Form.Control>
          </Form.Group>
          {/* <Form.Group>
            <Form.Label>Confirm Your Password:</Form.Label>
            <Form.Control
              type="password"
              ref={passwordConfirmRef}
              required
              onChange={(e) => setRegisterConfirmPassWord(e.target.value)}
            ></Form.Control>
          </Form.Group> */}

          <Button type="submit" className="w-100 my-3">
            Sign Up
          </Button>

          <Button className="w-100 mb-3" onClick={handleLogOut}>
            Log Out
          </Button>
          <div>Current user: {user && user.email}</div>
        </Form>

        <Form onSubmit={handleLogin}>
          <Form.Group>
            <Form.Label>Your Email:</Form.Label>
            <Form.Control
              type="email"
              required
              onChange={(e) => setLoginEmail(e.target.value)}
            ></Form.Control>
          </Form.Group>
          <Form.Group>
            <Form.Label>Your Password:</Form.Label>
            <Form.Control
              type="password"
              required
              onChange={(e) => setLoginPassword(e.target.value)}
            ></Form.Control>
            <Button className="w-100 mb-3" type="submit">
              Log In
            </Button>
          </Form.Group>
        </Form>
        <div className="">
          Already have an account? <Link to="/login">Login</Link>
        </div>
      </Card.Body>
    </Card>
  );
};

export default SignUp;
