import axios from "axios";
import { Fragment, useCallback, useReducer } from "react";
import { useEffect, useState, useRef } from "react";
import "./App.css";
import TodoApp from "./TodoApp";
import styled from "styled-components";
import { ReactComponent as Check } from "./svg/checked-mark.svg";
import React from  'react';

//CSS
const StlyedContainer = styled.div`
  padding: 20px;
  color: #171212;
  background: #83a4d4; /* Fallback for older browser */
  background: linear-gradient(to left, #b6fbff, #83a4d4);
`;
const StyledHeadlinePrimary = styled.h1`
  text-align: center;
  font-weight: 500;
  font-size: 48px;
  letter-spacing: 2px;
`;

const StyledItem = styled.div`
  padding-bottom: 20px;
  display: flex;
  align-items: center;
`;

const StyledColumn = styled.span`
  padding: 0 10px;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;

  a {
    color: inherit;
  }

  width: ${(props) => props.width};
`;

const StyledButton = styled.button`
  padding: 5px;
  border: 1px solid #171212;
  cursor: pointer;
  background: transparent;
  transition: all 0.1s ease-in;

  &:hover {
    background: #171212;
    color: #ffff;
    svg {
      g {
        fill: #ffffff;
        stroke: #ffffff;
      }
    }
  }
`;

const StyledButtonSmall = styled(StyledButton)`
  padding: 5px;
`;

const StyledButtonLarge = styled(StyledButton)`
  padding: 10px;
`;

const StyledSearchForm = styled.form`
  padding: 10px 0 20px 0;
  display: flex;
  align-items: stretch;
`;

const StyledLabel = styled.label`
  border-top: 1px solid #171212;
  border-left: 1px solid #171212;
  font-size: 24px;
  padding-left: 5px;
`;

const StyledInput = styled.input`
  width: 100%;
  border: none;
  border-bottom: 1px solid #171212;
  background-color: transparent;
  font-size: 24px;
  margin-left: -5px;
  padding-left: 5px;
`;

//Custom Hook
export const usePersistentState = (key, initialState) => {
  const [value, setValue] = useState(localStorage.getItem(key) || initialState);
  const isMounted = useRef(false);
  useEffect(() => {
    if (!isMounted.current) {
      isMounted.current = true;
    } else {
      console.log("A");
      localStorage.setItem(key, value);
    }
  }, [key, value]);

  return [value, setValue];
};

//Reducer
//Stories reducer
const storiesReducer = (state, action) => {
  switch (action.type) {
    case "STORIES_FETCH_INIT":
      return {
        ...state,
        isLoading: true,
        isError: false,
      };
    case "STORIES_FETCH_SUCCESS":
      return {
        ...state,
        isLoading: false,
        isError: false,
        data: action.payload,
      };
    case "STORIES_FETCH_FAILURE":
      return {
        ...state,
        isLoading: false,
        isError: true,
      };
    case "REMOVE_STORY":
      return {
        ...state,
        data: state.data.filter(
          (story) => story.objectID !== action.payload.objectID
        ),
      };
    default:
      throw new Error();
  }
};

const App = () => {
  const [stories, dispatchStories] = useReducer(storiesReducer, {
    data: [],
    isLoading: false,
    isError: false,
  });
  const [searchTerm, setSearchTerm] = usePersistentState("search", "React");
  const API_ENDPOINT = "https://hn.algolia.com/api/v1/search?query=";
  const [url, setUrl] = useState(`${API_ENDPOINT}${searchTerm}`);

  const handleFetchStories = useCallback(async () => {
    dispatchStories({
      type: "STORIES_FETCH_INIT",
    });

    try {
      const respone = await axios.get(url);
      dispatchStories({
        type: "STORIES_FETCH_SUCCESS",
        payload: respone.data.hits,
      });
    } catch {
      dispatchStories({
        type: "STORIES_FETCH_FAILURE",
      });
    }
  }, [url]);

  useEffect(() => {
    handleFetchStories();
  }, [handleFetchStories]);

  const handleRemoveStory = (item) => {
    dispatchStories({
      type: "REMOVE_STORY",
      payload: item,
    });
  };

  const handleSearchInput = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleSearchSubmit = (e) => {
    setUrl(`${API_ENDPOINT}${searchTerm}`);
    e.preventDefault();
  };

  console.log("B: App");
  return (
    <StlyedContainer>
      <TodoApp />

      <StyledHeadlinePrimary>My Hacker Stories</StyledHeadlinePrimary>

      <SearchForm
        searchTerm={searchTerm}
        onSearchSubmit={handleSearchSubmit}
        onSearchInput={handleSearchInput}
      />

      {stories.isError && (
        <p>
          <strong>Oops! Something went wrong...</strong>
        </p>
      )}

      {stories.isLoading ? (
        <p>
          <strong>Loading...</strong>
        </p>
      ) : (
        <List list={stories.data} onRemoveItem={handleRemoveStory} />
      )}
    </StlyedContainer>
  );
};

export default App;

const SearchForm = ({ onSearchSubmit, onSearchInput, searchTerm }) => (
  <StyledSearchForm onSubmit={onSearchSubmit}>
    <InputWithLabel
      onInputChange={onSearchInput}
      value={searchTerm}
      id="search"
      label="Search"
      isFocused={true}
    >
      <strong>Search for:</strong>
    </InputWithLabel>
    <StyledButtonLarge type="submit" disabled={!searchTerm}>
      Submit
    </StyledButtonLarge>
  </StyledSearchForm>
);

export const InputWithLabel = ({
  isFocused,
  children,
  type = "text",
  id,
  value,
  onInputChange,
  checked,
}) => {
  const inputRef = useRef(null);
  useEffect(() => {
    if (isFocused && inputRef.current) {
      inputRef.current.focus();
    }
  });

  return (
    <Fragment>
      <StyledLabel htmlFor={id}>
        {children}
        &nbsp;
        <StyledInput
          id={id}
          type={type}
          value={value}
          onChange={onInputChange}
          ref={inputRef}
          checked={checked}
        ></StyledInput>
      </StyledLabel>
    </Fragment>
  );
};

const List = React.memo(({ list, onRemoveItem }) =>
  console.log("B: List") || (
    <ul>
      {list.map((item) => (
        <Item key={item.objectID} item={item} onRemoveItem={onRemoveItem} />
      ))}
    </ul>
  )
);


const Item = ({ item, onRemoveItem }) => {
  return (
    <StyledItem>
      <StyledColumn width="40%">
        <a href={item.url}>{item.title}: </a>
      </StyledColumn>
      <StyledColumn width="20%">{item.author} </StyledColumn>
      <StyledColumn width="20%">
        <p>Comments: {item.num_comments}</p>
      </StyledColumn>
      <StyledColumn width="10%">
        <p>Points: {item.points}</p>
      </StyledColumn>

      <StyledColumn width="10%">
        <StyledButtonSmall type="button" onClick={() => onRemoveItem(item)}>
          <Check height="10px" width="10px" />
        </StyledButtonSmall>
      </StyledColumn>
    </StyledItem>
  );
};
