import React from "react";
import {
  BrowserRouter as Router,
  Route,
  Switch,
  NavLink,
  useHistory,
} from "react-router-dom";
import TodoApp from "./TodoApp";
import HackerNewStories from "./HackerNewStoriesApp";
import Home from "./Home";
import Login from "./Login";
import SignUp from "./SignUp";
import PrivateRoute from "./PrivateRoute";
import "./FontAwesome";
import "./App.css";
import ScrollTopArrow from "./ScrollTopArrow/ScrollTopArrow";
import { Navbar, Container, Nav, NavDropdown } from "react-bootstrap";
import { AuthProvider } from "../contexts/AuthContext";
import ForgotPassWord from "./ForgotPassword";
import UpdateProfile from "./UpdateProfile";
import Topics from "./Topics";
import NoMatch from "./NoMatch";
import { AppProvider } from "../contexts/AppContext";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { useAuth } from "../contexts/AuthContext";
import { auth } from "../firebase/config";

export const App = () => {
  const { user, logout } = useAuth();
  console.log("Current user: ", { user });

  const handleSignOut = async () => {
    if (user) {
      try {
        await logout(auth);
      } catch (error) {
        console.log(error);
      }
    }
  };
  return (
    <Router>
      <Navbar
        expand="sm"
        variant="dark"
        className="d-flex justify-content-start"
      >
        <Container fluid>
          <Navbar.Brand>
            <span className="me-2">
              <FontAwesomeIcon icon={["fab", "react"]} />
            </span>
            <span>My React App</span>
          </Navbar.Brand>

          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="mx-5">
              <Nav.Link className="">
                <NavLink exact to="/" className="" activeClassName="text-white">
                  Home
                </NavLink>
              </Nav.Link>

              <Nav.Link className="">
                <NavLink
                  to="/todo-app"
                  className=""
                  activeClassName="text-white"
                >
                  Todo App
                </NavLink>
              </Nav.Link>

              <Nav.Link className="">
                <NavLink
                  to="/stories-app"
                  className=""
                  activeClassName="text-white"
                >
                  Hacker News Stories App
                </NavLink>
              </Nav.Link>

              <Nav.Link className="">
                <NavLink to="/topics" className="" activeClassName="text-white">
                  Topics
                </NavLink>
              </Nav.Link>

              <NavDropdown
                title={<FontAwesomeIcon icon={["fas", "cog"]} size="sm" />}
                id="basic-nav-dropdown"
              >
                <span className="p-2" onClick={handleSignOut}>
                  Sign out
                </span>
              </NavDropdown>
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>

      <Container>
        <AuthProvider>
          <AppProvider>
            <Switch>
              <PrivateRoute exact path="/">
                <Home />
              </PrivateRoute>

              <PrivateRoute path="/todo-app">
                <TodoApp />
              </PrivateRoute>

              <PrivateRoute path="/stories-app">
                <HackerNewStories />
              </PrivateRoute>

              <Route path="/login">
                <Login />
              </Route>

              <Route path="/signup">
                <SignUp />
              </Route>

              <Route path="/forgot-password">
                <ForgotPassWord />
              </Route>

              <Route path="/update-profile">
                <UpdateProfile />
              </Route>

              <PrivateRoute path="/topics">
                <Topics />
              </PrivateRoute>

              <Route path="*">
                <NoMatch />
              </Route>
            </Switch>
          </AppProvider>
        </AuthProvider>

        <ScrollTopArrow />
      </Container>
    </Router>
  );
};

export default App;
