import React, { useEffect, useReducer, useState, useCallback } from "react";
import { List } from "./List";
import { AddTodoForm } from "./AddForm";
import { todoReducer } from "./reducer";
import axios from "axios";
import { Link } from "react-router-dom";
import styled from "styled-components";

const StyledMessage = styled.p`
  text-transform: capitalize;
  font-size: 18px;
  font-weight: 500;
  font-style: italic;
  margin-top: 1.5rem;
`;

const StyledHeadlinePrimary = styled.h1`
  text-align: center;
  font-weight: 500;
  font-size: 48px;
  letter-spacing: 2px;
`;

const API_ENDPOINT = "https://jsonplaceholder.typicode.com/todosa";
const PARAM_SEARCH = "_page=";

const getUrl = (page) => `${API_ENDPOINT}?${PARAM_SEARCH}${page}`;

const TodoApp = () => {
  const [todos, dispatchTodos] = useReducer(todoReducer, {
    data: [],
    isLoading: false,
    isError: false,
  });
  const [page, setPage] = useState(1);
  const sum_todos = todos.data.length;
  const [unDoneTodos, setUnDoneTodos] = useState(0);
  const [todoQuery, setTodoQuery] = useState("");
  const [url, setUrl] = useState(getUrl(page));

  const handleFetchTodos = useCallback(async () => {
    dispatchTodos({
      type: "TODOS_FETCH_INIT",
    });

    try {
      const respone = await axios.get(url);
      dispatchTodos({
        type: "TODOS_FETCH_SUCCESS",
        payload: {
          list: respone.data,
          page: page,
        },
      });
    } catch {
      dispatchTodos({
        type: "TODOS_FETCH_FAILURE",
      });
    }
  }, [url, page]);

  useEffect(() => handleFetchTodos(), [handleFetchTodos]);

  const handleMore = () => {
    setPage(page + 1);
    const url = getUrl(page + 1);
    setUrl(url);
  };

  useEffect(() => {
    setUnDoneTodos(
      todos.data.filter((todo) => todo.completed === false).length
    );
  }, [todos]);

  const handleCheckedTodo = (todo) => {
    dispatchTodos({
      type: todo.completed ? "UNDO_TODO" : "DO_TODO",
      id: todo.id,
    });
  };

  const handleRemoveTodo = (todo) => {
    dispatchTodos({
      type: "REMOVE_TODO",
      payload: todo,
    });
  };

  const handleAddTodo = (todoQuery) => {
    dispatchTodos({
      type: "ADD_TODO",
      payload: todoQuery,
    });
  };

  const handleAddTodoInput = (e) => {
    setTodoQuery(e.target.value);
  };

  const handleAddTodoSubmit = (e) => {
    e.preventDefault();
    handleAddTodo(todoQuery);
  };

  const handleCheckboxChange = (list, e) =>
    dispatchTodos({
      type: e.target.checked ? "MARK_ALL_TODOS" : "UNMARK_ALL_TODOS",
      payload: list,
    });

  return (
    <div>
      <nav className="d-flex justify-content-end">
        <ul className="nav">
          <li className="nav-item">
            <Link to="/">
              <span className="nav-link">Home</span>
            </Link>
          </li>
          <li className="nav-item">
            <Link to="/todo-app">
              <span className="nav-link">Todo App</span>
            </Link>
          </li>
          <li className="nav-item">
            <Link to="/stories-app">
              <span className="nav-link">Hacker News Stories App</span>
            </Link>
          </li>
        </ul>
      </nav>

      <StyledHeadlinePrimary>Helo</StyledHeadlinePrimary>

      <h1>My Todo with {sum_todos} works.</h1>

      <StyledMessage>{unDoneTodos} Works uncompleted!</StyledMessage>

      {todos.isError && (
        <StyledMessage>Oops! Something went wrong</StyledMessage>
      )}

      <List
        list={todos.data}
        onRemoveTodo={handleRemoveTodo}
        onCheckedTodo={handleCheckedTodo}
        onCheckboxChange={handleCheckboxChange}
      />

      {todos.isLoading ? (
        <StyledMessage>Loading...</StyledMessage>
      ) : (
        <button className="btn btn-primary" onClick={handleMore}>
          More
        </button>
      )}

      <AddTodoForm
        onAddTodoInput={handleAddTodoInput}
        onAddTodoSubmit={handleAddTodoSubmit}
      />
    </div>
  );
};
export default TodoApp;
