import React from "react";
import { sortBy } from "lodash";
// import { InputWithLabel } from "./InputWithLabel";

const SORTS = {
  NONE: (list) => list,
  TITLE: (list) => sortBy(list, "title"),
};

export const List = ({
  list,
  onRemoveTodo,
  onCheckedTodo,
  onCheckboxChange,
}) => {
  const [sort, setSort] = React.useState({
    sortKey: "NONE",
    isReversed: false,
  });
  const handleSort = (sortKey) => {
    const isReversed = sort.sortKey === sortKey && !sort.isReversed;
    setSort({ sortKey, isReversed });
  };

  const sortFunction = SORTS[sort.sortKey];
  const sortedList = sort.isReversed
    ? sortFunction(list).reverse()
    : sortFunction(list);

  return (
    <div>
      <div>
        <label htmlFor="markAllTodos">
          <input
            id="markAllTodos"
            type="checkbox"
            onChange={(e) => onCheckboxChange(list, e)}
          />

          <span>Mark all works</span>
        </label>
      </div>

      <div>
        <button className="btn btn-primary" type="button" onClick={() => handleSort("TITLE")}>
          Sort List
        </button>
      </div>

      <div style={{ listStyle: "none" }}>
        {sortedList.map((todo) => (
          <Item
            todo={todo}
            onRemoveTodo={onRemoveTodo}
            onCheckedTodo={onCheckedTodo}
            key={todo.id}
          />
        ))}
      </div>
    </div>
  );
};

const Item = ({ todo, onRemoveTodo, onCheckedTodo }) => {
  return (
    <div>
      <button onClick={() => onRemoveTodo(todo)}>Delete</button>

      <div>
        <label htmlFor={todo.id}>
          <span>{todo.title}</span>
        </label>
        &nbsp;
        <input
          value={todo.title}
          id={todo.id}
          type="checkbox"
          onChange={() => onCheckedTodo(todo)}
          checked={todo.completed}
        />
      </div>

      {/*   <InputWithLabel
        id={todo.id}
        value={todo.title}
        checked={todo.completed}
        type="checkbox"
        onInputChange={() => onCheckedTodo(todo)}
      >
        <span
          style={{
            whiteSpace: "nowrap",
            overFlow: "hidden",
            textOverflow: "ellipsis",
          }}
        >
          {todo.title}
        </span>
      </InputWithLabel> */}
    </div>
  );
};
