import { useEffect, useState } from "react";
import { db } from "../../firebase/config";
import {
  collection,
  onSnapshot,
  query,
  orderBy,
  where,
} from "firebase/firestore";

// export function useFilestore(FirestoreCollection, condition) {
//   const [documents, setDocuments] = useState([]);
//   useEffect(() => {
//     let collectionRef = collection(db, FirestoreCollection);
//     const q = query(collectionRef, orderBy("createdAt"), condition);
//     const unsubcribe = onSnapshot(q, (snapshot) => {
//       if (condition) {
//         if (!condition.compareValue || !condition.compareValue.length) {
//           return;
//         }
//         collectionRef = collectionRef.where(
//           condition.fieldValue,
//           condition.operator,
//           condition.compareValue
//         );
//       }

//       const data = snapshot.docs.map((doc) => ({
//         ...doc.data(),
//         id: doc.id,
//       }));
//       setDocuments(data);
//     });
//     return unsubcribe;
//   }, [collections, condition]);

//   return documents;
// }

export default function useFilestore(FirestoreCollection, condition) {
  const [documents, setDocuments] = useState([]);
  useEffect(() => {
    //Listen to multiple documents in collection Users
    let collectionRef = collection(db, FirestoreCollection);
    let q = query(collectionRef, orderBy("createAt"));

    if (condition) {
      if (!condition.compareValue || !condition.compareValue.length) {
        return;
      }

      q = query(
        collectionRef,
        orderBy("createdAt"),
        where(condition.fieldValue, condition.operator, condition.compareValue)
      );
    }

    const unsub = onSnapshot(q, (querySnapshot) => {
      const data = querySnapshot.docs.map((doc) => ({
        ...doc.data(),
        id: doc.id,
      }));
      setDocuments(data);
    });

    return unsub;
  }, [FirestoreCollection, condition]);
  return documents;
}
