import { ReactComponent as Check } from "../../svg/checked-mark.svg";
import * as SC from "./style";
import React from "react";
import { sortBy } from "lodash";
import InfiniteScroll from "react-infinite-scroll-component";
import { ReactComponent as ArrowUp } from "../../svg/arrow-up.svg";
import { ReactComponent as ArrowDown } from "../../svg/arrow-down.svg";
/* import Pagination from "./Pagination";
let PageSize = 10;
 */
const SORTS = {
  NONE: (list) => list,
  TITLE: (list) => sortBy(list, "title"),
  AUTHOR: (list) => sortBy(list, "author"),
  NUM_COMMENTS: (list) => sortBy(list, "num_comments").reverse(),
  POINTS: (list) => sortBy(list, "points").reverse(),
};

export const List = React.memo(({ list, onRemoveItem, onFetchMore, page }) => {
  const [sort, setSort] = React.useState({
    sortKey: "NONE",
    isReversed: false,
    isSorted: false,
  });

  const handleSort = (sortKey) => {
    const isReversed = sortKey === sort.sortKey && !sort.isReversed;

    /*  setSort({ sortKey: sortKey, isReversed: isReversed }); */
    setSort({ sortKey, isReversed, isSorted: true }); //JS Object initializers
  };

  const sortFunction = SORTS[sort.sortKey];
  const sortedList = sort.isReversed
    ? sortFunction(list).reverse()
    : sortFunction(list);

  /* const [currentPage, setCurrentPage] = React.useState(page);

  const currentStories = React.useMemo(() => {
    const firstPageIndex = (currentPage - 1) * PageSize;
    const lastPageIndex = firstPageIndex + PageSize;
    return sortedList.slice(firstPageIndex, lastPageIndex);
  }, [currentPage, sortedList]); */

  /* const checkFetchMore = () => {
    const list = document.getElementById("list");
    if (
      window.innerHeight + window.scrollY ===
      list.clientHeight + list.offsetTop
    ) {
      setLoadMore(true)
      console.log(list.offsetTop);
      console.log(list.clientHeight);
      console.log(list.offsetTop + list.clientHeight);
      console.log(window.innerHeight + window.scrollY);
      console.log(window.innerHeight);
      console.log(window.scrollY);
    } 
  };

  React.useEffect(() => {
    window.addEventListener("scroll", checkFetchMore);
    return () => {
      window.removeEventListener("scroll", checkFetchMore);
    };
  }); */

  const [loadMore, setLoadMore] = React.useState(true);

  React.useEffect(() => {
    getData(loadMore);
    setLoadMore(false);
  }, [loadMore]);

  React.useEffect(() => {
    const list = document.getElementById("list");

    // list has auto height
    window.addEventListener("scroll", () => {
      if (
        window.scrollY + window.innerHeight >=
        list.clientHeight + list.offsetTop
      ) {
        setLoadMore(true);
      }
    });
  }, []);

  /* React.useEffect(() => {
    const list = document.getElementById("list");

    if (list.clientHeight <= window.innerHeight && list.clientHeight) {
      setLoadMore(true);
    }
  }, []); */

  const getData = (load) => {
    if (load) {
      onFetchMore();
    }
  };

  return (
    <div>
      <div>
        <SC.StyledItem>
          <SC.StyledButtonSmall
            type="button"
            onClick={() => handleSort("TITLE")}
            style={{ width: "40%" }}
          >
            Title
            {sort.isSorted & (sort.sortKey === "TITLE") ? (
              sort.isReversed ? (
                <ArrowDown height="15" width="15" />
              ) : (
                <ArrowUp height="15" width="15" />
              )
            ) : null}
          </SC.StyledButtonSmall>

          <SC.StyledButtonSmall
            type="button"
            onClick={() => handleSort("AUTHOR")}
            style={{ width: "20%" }}
          >
            Author
            {sort.isSorted & (sort.sortKey === "AUTHOR") ? (
              sort.isReversed ? (
                <ArrowDown height="15" width="15" />
              ) : (
                <ArrowUp height="15" width="15" />
              )
            ) : null}
          </SC.StyledButtonSmall>

          <SC.StyledButtonSmall
            type="button"
            onClick={() => handleSort("NUM_COMMENTS")}
            style={{ width: "20%" }}
          >
            Comments
            {sort.isSorted & (sort.sortKey === "NUM_COMMENTS") ? (
              sort.isReversed ? (
                <ArrowUp height="15" width="15" />
              ) : (
                <ArrowDown height="15" width="15" />
              )
            ) : null}
          </SC.StyledButtonSmall>

          <SC.StyledButtonSmall
            type="button"
            onClick={() => handleSort("POINTS")}
            style={{ width: "10%" }}
          >
            Points
            {sort.isSorted & (sort.sortKey === "POINTS") ? (
              sort.isReversed ? (
                <ArrowUp height="15" width="15" />
              ) : (
                <ArrowDown height="15" width="15" />
              )
            ) : null}
          </SC.StyledButtonSmall>

          <SC.StyledButtonSmall style={{ width: "10%" }}>
            Actions
          </SC.StyledButtonSmall>
        </SC.StyledItem>
      </div>

      {/* <div>
        {currentStories.map((item) => (
          <Item key={item.objectID} item={item} onRemoveItem={onRemoveItem} />
        ))}
      </div>

      <Pagination
        totalCount={sortedList.length}
        currentPage={currentPage}
        pageSize={PageSize}
        onPageChange={(page) => setCurrentPage(page)}
        onFetchMore={onFetchMore}
      /> */}

      {/* <InfiniteScroll //Infinite Scroll Fetch API
        dataLength={sortedList.length}
        next={onFetchMore}
        hasMore={true}
        endMessage={
          <p style={{ textAlign: "center" }}>
            <b>Yay! You have seen it all</b>
          </p>
        }
      >
        {sortedList.map((item) => (
          <Item key={item.objectID} item={item} onRemoveItem={onRemoveItem} />
        ))}
      </InfiniteScroll> */}

      <div id="list" style={{ background: "pink" }}>
        {sortedList.map((item) => (
          <Item key={item.objectID} item={item} onRemoveItem={onRemoveItem} />
        ))}
      </div>
    </div>
  );
});

const Item = React.memo(({ item, onRemoveItem }) => (
  <SC.StyledItem>
    <SC.StyledColumn style={{ textAlign: "left", width: "40%" }}>
      <a href={item.url}>{item.title}</a>
    </SC.StyledColumn>
    <SC.StyledColumn style={{ textTransform: "capitalize", width: "20%" }}>
      {item.author}
    </SC.StyledColumn>
    <SC.StyledColumn width="20%">{item.num_comments}</SC.StyledColumn>
    <SC.StyledColumn width="10%">{item.points}</SC.StyledColumn>

    <SC.StyledColumn width="10%">
      <SC.StyledButtonSmall type="button" onClick={() => onRemoveItem(item)}>
        <Check height="10px" width="10px" />
      </SC.StyledButtonSmall>
    </SC.StyledColumn>
  </SC.StyledItem>
));
