import { ReactComponent as Check } from "../svg/checked-mark.svg";
import * as SC from "./style";
import React from "react";
import { sortBy } from "lodash";
import InfiniteScroll from "react-infinite-scroll-component";
import { ReactComponent as ArrowUp } from "../svg/arrow-up.svg";
import { ReactComponent as ArrowDown } from "../svg/arrow-down.svg";
import { useEffect, useState } from "react";
import ReactPaginate from "react-paginate";

const SORTS = {
  NONE: (list) => list,
  TITLE: (list) => sortBy(list, "title"),
  AUTHOR: (list) => sortBy(list, "author"),
  NUM_COMMENTS: (list) => sortBy(list, "num_comments").reverse(),
  POINTS: (list) => sortBy(list, "points").reverse(),
};

export const List = React.memo(({ list, onRemoveItem, onFetchMore }) => {
  const [sort, setSort] = React.useState({
    sortKey: "NONE",
    isReversed: false,
    isSorted: false,
  });

  const handleSort = (sortKey) => {
    const isReversed = sortKey === sort.sortKey && !sort.isReversed;

    /*  setSort({ sortKey: sortKey, isReversed: isReversed }); */
    setSort({ sortKey, isReversed, isSorted: true }); //JS Object initializers
  };

  const sortFunction = SORTS[sort.sortKey];
  const sortedList = sort.isReversed
    ? sortFunction(list).reverse()
    : sortFunction(list);

  return (
    <div>
      <div>
        <SC.StyledItem>
          <SC.StyledButtonSmall
            type="button"
            onClick={() => handleSort("TITLE")}
            style={{ width: "40%" }}
          >
            Title
            {sort.isSorted & (sort.sortKey === "TITLE") ? (
              sort.isReversed ? (
                <ArrowDown height="15" width="15" />
              ) : (
                <ArrowUp height="15" width="15" />
              )
            ) : null}
          </SC.StyledButtonSmall>

          <SC.StyledButtonSmall
            type="button"
            onClick={() => handleSort("AUTHOR")}
            style={{ width: "20%" }}
          >
            Author
            {sort.isSorted & (sort.sortKey === "AUTHOR") ? (
              sort.isReversed ? (
                <ArrowDown height="15" width="15" />
              ) : (
                <ArrowUp height="15" width="15" />
              )
            ) : null}
          </SC.StyledButtonSmall>

          <SC.StyledButtonSmall
            type="button"
            onClick={() => handleSort("NUM_COMMENTS")}
            style={{ width: "20%" }}
          >
            Comments
            {sort.isSorted & (sort.sortKey === "NUM_COMMENTS") ? (
              sort.isReversed ? (
                <ArrowUp height="15" width="15" />
              ) : (
                <ArrowDown height="15" width="15" />
              )
            ) : null}
          </SC.StyledButtonSmall>

          <SC.StyledButtonSmall
            type="button"
            onClick={() => handleSort("POINTS")}
            style={{ width: "10%" }}
          >
            Points
            {sort.isSorted & (sort.sortKey === "POINTS") ? (
              sort.isReversed ? (
                <ArrowUp height="15" width="15" />
              ) : (
                <ArrowDown height="15" width="15" />
              )
            ) : null}
          </SC.StyledButtonSmall>

          <SC.StyledButtonSmall style={{ width: "10%" }}>
            Actions
          </SC.StyledButtonSmall>
        </SC.StyledItem>
      </div>

      {/* <InfiniteScroll //Infinite Scroll Fetch API
        dataLength={sortedList.length}
        next={onFetchMore}
        hasMore={true}
        endMessage={
          <p style={{ textAlign: "center" }}>
            <b>Yay! You have seen it all</b>
          </p>
        }
      >
        {sortedList.map((item) => (
          <Item key={item.objectID} item={item} onRemoveItem={onRemoveItem} />
        ))}
      </InfiniteScroll> */}
      
      <PaginatedItems itemsPerPage={4} items={sortedList} />
    </div>
  );
});

export const Item = React.memo(({ item, onRemoveItem }) => (
  <SC.StyledItem>
    <SC.StyledColumn style={{ textAlign: "left", width: "40%" }}>
      <a href={item.url}>{item.title}</a>
    </SC.StyledColumn>
    <SC.StyledColumn style={{ textTransform: "capitalize", width: "20%" }}>
      {item.author}
    </SC.StyledColumn>
    <SC.StyledColumn width="20%">{item.num_comments}</SC.StyledColumn>
    <SC.StyledColumn width="10%">{item.points}</SC.StyledColumn>

    <SC.StyledColumn width="10%">
      <SC.StyledButtonSmall type="button" onClick={() => onRemoveItem(item)}>
        <Check height="10px" width="10px" />
      </SC.StyledButtonSmall>
    </SC.StyledColumn>
  </SC.StyledItem>
));

/* function Items({ currentItems }) {
  return (
    <>
      {currentItems &&
        currentItems.map((item) => (
          <div>
            <h3>Item #{item}</h3>
          </div>
        ))}
    </>
  );
} */

function PaginatedItems({ itemsPerPage, items }) {
  // We start with an empty list of items.
  const [currentItems, setCurrentItems] = useState(null);
  const [pageCount, setPageCount] = useState(0);
  // Here we use item offsets; we could also use page offsets
  // following the API or data you're working with.
  const [itemOffset, setItemOffset] = useState(0);

  useEffect(() => {
    // Fetch items from another resources.
    const endOffset = itemOffset + itemsPerPage;
    console.log(`Loading items from ${itemOffset} to ${endOffset}`);
    setCurrentItems(items.slice(itemOffset, endOffset));
    setPageCount(Math.ceil(items.length / itemsPerPage));
  }, [itemOffset, itemsPerPage, items]);

  // Invoke when user click to request another page.
  const handlePageClick = (event) => {
    const newOffset = (event.selected * itemsPerPage) % items.length;
    console.log(
      `User requested page number ${event.selected}, which is offset ${newOffset}`
    );
    setItemOffset(newOffset);
  };

  return (
    <>
      <List sortedList={currentItems} />
      <ReactPaginate
        breakLabel="..."
        nextLabel="next >"
        onPageChange={handlePageClick}
        pageRangeDisplayed={5}
        pageCount={pageCount}
        previousLabel="< previous"
        renderOnZeroPageCount={null}
      />
    </>
  );
}
