import React, { useState } from "react";
import { Button, Alert, Card, Modal } from "react-bootstrap";
import { auth } from "../../firebase/config";
import { useAuth } from "../../contexts/AuthContext";
import { Link, useHistory, Route } from "react-router-dom";

import UploadFileModal from "./UploadModal";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import UpdateProfile from "../UpdateProfile";

export const ProfilePage = () => {
  const history = useHistory();
  return (
    <>
      <Route path="/profile">
        <Profile />
      </Route>
      
      <Route
        path="/profile/update-profile"
        children={({ match }) => {
          return (
            <Modal show={Boolean(match)} onHide={history.goBack}>
              <UpdateProfile />
            </Modal>
          );
        }}
      />
    </>
  );
};

export const Profile = () => {
  const { logout, user } = useAuth();
  const [error, setError] = useState("");

  const handleLogOut = async () => {
    setError("");

    try {
      await logout(auth);
    } catch (error) {
      setError(error.message);
    }
  };

  return (
    <Card className="d-flex flex-row justify-content-center align-items-center">
      <Card.Body style={{ maxWidth: "400px" }}>
        <Card.Header as="h1" className="mb-4">
          Profile
        </Card.Header>

        {error && <Alert variant="danger">{error}</Alert>}

        <UploadFileModal />

        <Card.Text className="text-center">
          <strong>Email:</strong> {user.email}
          <Link to="/profile/update-profile" className="list-style-none ms-3">
            <FontAwesomeIcon icon={["fas", "pen"]} />
          </Link>
        </Card.Text>

        <Card.Text className="text-center">
          <strong>Name:</strong> {user.displayName}
          <Link to="/profile/update-profile" className="list-style-none ms-3">
            <FontAwesomeIcon icon={["fas", "pen"]} />
          </Link>
        </Card.Text>

        <div className="text-center mb-3">
          <Link to="/update-profile" className="text-white list-style-none">
            <Button>Update Profile</Button>
          </Link>
        </div>

        <div className="text-center w-100 mb-3">
          <Button onClick={handleLogOut}>Log Out</Button>
        </div>
      </Card.Body>
    </Card>
  );
};

export default ProfilePage;
