import React, { useState } from "react";
import {
  ProgressBar,
  Alert,
  Button,
  Row,
  Col,
  Container,
  Image,
  Modal,
  Card,
  Form,
} from "react-bootstrap";
import { storage } from "../../firebase/config";
import { ref, uploadBytesResumable, getDownloadURL } from "firebase/storage";
import { useAuth } from "../../contexts/AuthContext";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { v1 as uuidv4 } from "uuid";

export default function Homepage() {
  const [images, setImages] = useState([]);
  const [urls, setUrls] = useState([]);
  const [progress, setProgress] = useState();
  const [error, setError] = useState();
  const { user } = useAuth();
  const [show, setShow] = useState(false);

  const handleShow = () => setShow(true);
  const handleClose = () => setShow(false);

  const handleInputChange = (e) => {
  
    console.log(e.target.files);
  };

  const handleUploadImages = (path) => {
    setProgress(true);
    images.forEach((image) => {
      const imagesRef = ref(storage, user.email + `/${path}/` + image.name);
      const metadata = {
        contentType: "image/jpeg",
      };

      const uploadTask = uploadBytesResumable(imagesRef, image, metadata);
      uploadTask.on(
        "state_changed",
        (snapshot) => {
          const progress =
            (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
          setProgress(progress);
        },
        (error) => setError(error.message),
        async () => {
          const url = await getDownloadURL(imagesRef);
          setUrls((prevState) => [...prevState, url]);
        }
      );
    });
  };

  return (
    <Container>
      {error && <Alert variant="danger">{error}</Alert>}

      <h1>Homepage</h1>

      <Card>
        <Card.Body>Alo Alo</Card.Body>
        <Card.Footer>
          <div>
            <span>Add Images</span>
            <FontAwesomeIcon icon={["fas", "images"]} onClick={handleShow} />
          </div>
        </Card.Footer>
      </Card>

      <Modal show={show} onHide={handleClose}>
        <Modal.Body>
          <Form>
            <Form.Group>
              <Form.Label htmlFor="images">Select a photo</Form.Label>
              <Form.Control
                id="images"
                multiple
                type="file"
                onChange={(e) => {
                  handleInputChange(e);
                }}
                accept=".jpg, .jpeg, .png"
              ></Form.Control>
            </Form.Group>
          </Form>

          {progress && (
            <ProgressBar
              now={progress}
              max="100"
              label={`Loading...${progress}%`}
            />
          )}

          <Row>
            {urls.map((url, index) => (
              <Col xs={6} key={url + index} style={{}}>
                <Image fluid src={url} alt={url} style={{ height: "50vw" }} />
              </Col>
            ))}
          </Row>
        </Modal.Body>

        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleUploadImages("Images")}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
    </Container>
  );
}
