import React, { useEffect, useReducer, useState, useCallback } from "react";
import { List } from "./List";
import { AddTodoForm } from "./AddForm";
import { todoReducer } from "./reducer";
import axios from "axios";
import { Link } from "react-router-dom";
import * as SC from "./style";

const API_ENDPOINT = "https://jsonplaceholder.typicode.com/todos";
const PARAM_SEARCH = "_page=";
const PARAM_USERIDSEARCH = "userId=";

const getUrl = (page) => `${API_ENDPOINT}?${PARAM_SEARCH}${page}`;

const getUrlByUserId = (page, searchId) =>
  `${API_ENDPOINT}?${PARAM_SEARCH}${page}&${PARAM_USERIDSEARCH}${searchId}`;

// Storage SearchId Hook
const useSemiPersistentState = (key, initialState) => {
  const [value, setValue] = useState(localStorage.getItem(key) || initialState);

  useEffect(() => {
    localStorage.setItem(key, value);
  });

  return [value, setValue];
};

const TodoApp = () => {
  const [todos, dispatchTodos] = useReducer(todoReducer, {
    data: [],
    isLoading: false,
    isError: false,
  });

  const sum_todos = todos.data.length;
  const [unDoneTodos, setUnDoneTodos] = useState(0);
  const [addTodoQuery, setAddTodoQuery] = useState("");
  const [page, setPage] = useState(1);
  const [searchId, setSearchId] = useSemiPersistentState("IdSearch", 1);
  const [urls, setUrls] = useState([getUrl(page, searchId)]);
  const [isShowedAll, setIsShowedAll] = useState(false);

  const handleFetchTodos = useCallback(async () => {
    dispatchTodos({
      type: "TODOS_FETCH_INIT",
    });

    try {
      const lastUrl = urls[urls.length - 1];
      const respone = await axios.get(lastUrl);
      dispatchTodos({
        type: "TODOS_FETCH_SUCCESS",
        payload: {
          list: respone.data,
          page: page,
        },
      });
    } catch {
      dispatchTodos({
        type: "TODOS_FETCH_FAILURE",
      });
    }
  }, [urls, page]);

  useEffect(() => handleFetchTodos(), [handleFetchTodos]);

  useEffect(() => {
    localStorage.setItem("search", searchId);
  }, [searchId]);

  const handleMore = () => {
    if (!isShowedAll) {
      const url = getUrlByUserId(page + 1, searchId);
      setUrls(urls.concat(url));
    } else {
      const url = getUrl(page + 1);
      setUrls(urls.concat(url));
    }
    setPage(page + 1);
  };

  const handleCheckedTodo = (todo) => {
    dispatchTodos({
      type: todo.completed ? "UNDO_TODO" : "DO_TODO",
      id: todo.id,
    });
  };

  const handleRemoveTodo = (todo) => {
    dispatchTodos({
      type: "REMOVE_TODO",
      payload: todo,
    });
  };

  const handleAddTodo = (todoQuery) => {
    dispatchTodos({
      type: "ADD_TODO",
      payload: todoQuery,
    });
  };

  const handleAddTodoInput = (e) => {
    setAddTodoQuery(e.target.value);
  };

  const handleAddTodoSubmit = (e) => {
    e.preventDefault();
    handleAddTodo(addTodoQuery);
  };

  const handleCheckboxChange = (list, e) =>
    dispatchTodos({
      type: e.target.checked ? "MARK_ALL_TODOS" : "UNMARK_ALL_TODOS",
      payload: list,
    });

  const handleSearchIdInput = (e) => {
    setSearchId(e.target.value);
  };

  const handleSearchIdSubmit = (e) => {
    setPage(1);
    setIsShowedAll(false);
    const url = getUrlByUserId(1, searchId);
    setUrls(urls.concat(url));
    e.preventDefault();
  };

  const handleShowAll = () => {
    setSearchId("");
    const url = getUrl(1);
    setUrls(urls.concat(url));
    setIsShowedAll(true);
    setPage(1);
  };

  useEffect(() => {
    setUnDoneTodos(
      todos.data.filter((todo) => todo.completed === false).length
    );
  }, [todos]);

  return (
    <div>
      <SC.StyledNavbar className="d-flex justify-content-end">
        <ul className="nav">
          <li className="nav-item">
            <Link to="/">
              <span className="nav-link">Home</span>
            </Link>
          </li>
          <li className="nav-item">
            <Link to="/todo-app">
              <span className="nav-link">Todo App</span>
            </Link>
          </li>
          <li className="nav-item">
            <Link to="/stories-app">
              <span className="nav-link">Hacker News Stories App</span>
            </Link>
          </li>
        </ul>
      </SC.StyledNavbar>

      <h1>My Todo with {sum_todos} works.</h1>

      <SC.StyledMessage>{unDoneTodos} Works uncompleted!</SC.StyledMessage>

      <SC.StyledForm className="form mb-3" onSubmit={handleSearchIdSubmit}>
        <SC.StyledLabel
          className="form-group text-capitalize"
          htmlFor="IdSearch"
        >
          Search by UserId:
        </SC.StyledLabel>
        <div className="row">
          <div className="col-10" style={{ marginRight: "-0.75rem" }}>
            <input
              id="IdSearch"
              className="form-group form-control"
              type="text"
              placeholder="Your ID number"
              autoFocus
              onChange={handleSearchIdInput}
              value={searchId}
            />
          </div>
          <div className="col-2">
            <button className="btn btn-primary">Submit</button>
          </div>
        </div>
      </SC.StyledForm>

      <button className="btn btn-primary mt-2 mb-3" onClick={handleShowAll}>
        Show All User's Works
      </button>

      <List
        list={todos.data}
        onRemoveTodo={handleRemoveTodo}
        s
        onCheckedTodo={handleCheckedTodo}
        onCheckboxChange={handleCheckboxChange}
        UserId={searchId}
      />

      {todos.isError && (
        <SC.StyledMessage>Oops! Something went wrong</SC.StyledMessage>
      )}

      {todos.isLoading ? (
        <SC.StyledMessage>Loading...</SC.StyledMessage>
      ) : (
        <button className="btn btn-primary" onClick={handleMore}>
          More
        </button>
      )}

      <AddTodoForm
        onAddTodoInput={handleAddTodoInput}
        onAddTodoSubmit={handleAddTodoSubmit}
      />
    </div>
  );
};
export default TodoApp;
